import React from 'react';

import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

import DoctorShow from './components/DoctorShow';
import Doctors from './containers/Doctors';

import Profile from './components/Profile';
import Search from './components/Search';
import './App.css';
import {
  fetchApiDoctors,
  createReviews,
  createPhotos,
} from './helpers/helpers';
import axios from 'axios';

import Landing from './components/layout/Landing';
import SignUp from './components/SignUp';
import Login from './components/Login';

import NavBar from './components/layout/NavBar';
import { Container } from 'semantic-ui-react';

class App extends React.Component {
  state = {
    loading: false,
    favorite: 0,
    currentUser: null,
    lat: 0,
    lng: 0,
    doctors: [],
    searchedDocs: [],

    apiDoctors: [],

    doctorShow: {},
    doctorSearch: [],

  };

  doneLoading = () => {
    this.setState({ loading: false }, () => {
      console.log('loading = false ?', this.state.loading);
    });
  };

  googleSearch = async (payload) => {
    this.setState({ loading: true });
    const apidocs = await fetchApiDoctors(payload);

    this.setState({ doctorSearch: apidocs, loading: false }, () =>
      this.props.history.push('/search/doctors')
    );

    await apidocs.forEach(async (doc) => {
      let reviews = await createReviews(doc.place_id, doc.id);
      if (reviews) {
        doc.reviews = reviews;
      }
    });
    await apidocs.map(async (doc) => {
      let photos = await createPhotos(doc.place_id, doc.id);
      if (photos) {
        doc.photos = photos;
      }
    });
    this.setState({ loading: false });
  };

  isFavorite = () => {
    let favoriteArray = this.state.currentUser.doctors.filter(
      (doctor) => doctor.api_id == this.props.match.params.id
    );
    if (favoriteArray.length > 0) {
      this.setState({
        favorite: 1,
      });
    }
  };

  heart = (doctor) => {
    console.log(doctor);
    console.log('got here in heart');
  };

  unHeart = (favorite) => {
    // debugger

    // let favorite = this.state.currentUser.userFavorites.find(favorite => favorite.api_id === doctor.uid)
    fetch(
      `https://cryptic-island-45793.herokuapp.com/favorites/${favorite.id}`,
      {
        method: 'DELETE',
      }
    )
      .then((resp) => resp.json())
      .then((data) => {
        let favorites = this.state.currentUser.favorites.filter(
          (favorite) => favorite.id !== data.id
        );
        let doctors = this.state.currentUser.doctors.filter(
          (doctor) => doctor.api_id !== data.api_id
        );
        this.setState({
          ...this.state,
          favorite: 0,
          doctors: [data.doctor, ...this.state.doctors],
          currentUser: {
            ...this.state.currentUser,
            favorites: favorites,
            doctors: doctors,
          },
        });
      });
  };



  // request to Google GeoCode API to turn string into Longitude/Latitude

  // request to BetterDoc API with Long/Lat


  patchUser = async (formData) => {
    this.setState({ loading: true });
    let res = await axios.patch(
      `http://localhost:3000/${this.state.currentUser.id}`,
      formData
    );
    let currentUser = res.data;
    this.setState({ currentUser, loading: false });
  };
  getDoctorById = async (id) => {
    // debugger
    try {
      let res = await axios.get(`http://localhost:3000/doctors/${id}`);

      let doctorShow = res.data;
      this.setState({ doctorShow, loading: false });
    } catch (err) {
      console.log(err);
      throw err;
    }
  };

  getDoctors = async () => {
    try {
      let res = await axios.get(`http://localhost:3000/doctors`);
      this.setState({ doctors: res.data });
    } catch (error) {
      console.log(error);
      throw error;
    }
  };
  loadUser = async () => {
    const token = localStorage.token;
    if (token) {
      //get user info
      try {
        let res = await axios.get('http://localhost:3000/auto_login', {
          headers: { Authorization: token },
        });
        this.setState({ currentUser: res.data });
      } catch (error) {
        // alert(error);
      }
    }
  };

  async componentDidMount() {
    // this.getDoctors();
    // this.loadUser();
  }

  login = async (formData) => {
    try {
      let res = await axios.post(`http://localhost:3000/login`, formData);
      if (res.errors) {
        alert(res.errors);
      } else {
        let currentUser = res.data;
        this.setUser(currentUser);
      }
    } catch (err) {
      alert(err);
      throw err;
    }
  };
  signUp = async (formData) => {
    try {
      let res = await axios.post(`http://localhost:3000/users`, formData);
      if (res.errors) {
        alert(res.errors);
      } else {
        let currentUser = res.data;
        this.setUser(currentUser);
      }
    } catch (err) {
      alert(err);
    }
  };
  setUser = (currentUser) => {
    this.setState(
      {
        currentUser: currentUser.user,
      },
      () => {
        localStorage.token = currentUser.token;
        // this.loadUser();
        this.props.history.push('/search');
      }
    );
  };

  logout = () => {
    this.setState(
      {
        currentUser: null,
      },
      () => {
        localStorage.removeItem('token');
        this.props.history.push('/');
      }
    );
  };
  render() {
    console.log('THIS.STATE FROM APP', this.state);
    return (
      <>
        <Container>
          <Router>
            <NavBar
              signUp={this.signUp}
              logout={this.logout}
              currentUser={this.state.currentUser}
            />
            <Switch>
              <Route exact path='/' component={Landing} />
              <Route
                exact
                path='/doctors'
                render={(routerProps) => (
                  <Doctors
                    loadUser={this.loadUser}
                    getDoctorById={this.getDoctorById}
                    currentUser={this.state.currentUser}
                    loading={this.state.loading}
                    getDoctors={this.getDoctors}
                    {...routerProps}
                    doctors={this.state.doctors}
                  />
                )}
              />
              <Route
                exact
                path='/signup'
                render={(routerProps) => (
                  <SignUp
                    {...routerProps}
                    setUser={this.setUser}
                    signUp={this.signUp}
                  />
                )}
              />

              <Route
                exact
                path='/login'
                render={(routerProps) => (
                  <Login
                    {...routerProps}
                    setUser={this.setUser}
                    login={this.login}
                  />
                )}
              />
              <Route
                exact
                path='/search'
                render={(routerProps) => (
                  <Search
                    error={this.state.error}
                    isLoading={this.state.isLoading}
                    currentUser={this.state.currentUser}
                    {...routerProps}
                    googleSearch={this.googleSearch}
                    favorite={this.favorite}
                    doneLoading={this.doneLoading}
                  />
                )}
              />
              <Route
                exact
                path='/search/doctors'
                render={(routerProps) => (
                  <Doctors
                    doctorSearch={this.state.doctorSearch}
                    loadUser={this.loadUser}
                    getDoctorById={this.getDoctorById}
                    currentUser={this.state.currentUser}
                    loading={this.state.loading}
                    getDoctors={this.getDoctors}
                    {...routerProps}
                  />
                )}
              />
              <Route
                exact
                path='/doctors/:id'
                render={(routerProps) => (
                  <DoctorShow
                    loadUser={this.loadUser}
                    getDoctorById={this.getDoctorById}
                    doctorShow={this.state.doctorShow}
                    rate={this.rate}
                    doctors={this.state.doctors}
                    favorite={this.state.favorite}
                    isFavorite={this.isFavorite}
                    heart={this.heart}
                    currentUser={this.state.currentUser}
                    {...routerProps}
                    favorite={this.favorite}
                  />
                )}
              />
              <Route
                exact
                path='/profile'
                render={(routerProps) => (
                  <Profile
                    patchUser={this.patchUser}
                    currentUser={this.state.currentUser}
                    doctors={this.state.doctors}
                    loadUser={this.loadUser}
                    {...routerProps}
                  />
                )}
              />
            </Switch>
          </Router>
        </Container>
      </>
    );
  }
}

export default App;
