import { Card, Grid } from 'semantic-ui-react';
import DoctorCard from '../components/DoctorCard';
import Axios from 'axios';

import React, { Component } from 'react';

export default class SearchIndex extends Component {
  loadSearchIndex = async () => {
    const index = [];
    const searchId = parseInt(this.props.match.params.searchId);
    const searches = this.props.currentUser.searches;
    let csv = searches[searchId].csv;

    csv = csv.split(',');
    csv.pop();

    await csv.map(async (id) => {
      // console.log(id);
      let doctorId = parseInt(id);
      let res = await Axios.get(`http://localhost:3000/doctors/${doctorId}`);
      let doc = res.data;

      // console.log(doc);
      index.push(doc);
      this.props.setSearchIndex(index);
    });
  };

  displayDoctors = () => {
    return this.props.searchIndex.map((doctor) => (
      <DoctorCard
        currentUser={this.props.currentUser}
        getDoctorById={this.props.getDoctorById}
        key={doctor.id}
        doctor={doctor}
      />
    ));
  };

  async componentDidMount() {
    await this.props.loadUser();
    this.loadSearchIndex();
  }
  render() {
    return (
      <div>
        {' '}
        <Grid
          container
          textAlign='center'
          style={{ height: '100vh' }}
          verticalAlign='middle'
        >

          <Card.Group
            style={{ maxHeight: '35em', padding: '3em' }}
            display='flex'
            justify-content='center'
            itemsPerRow={3}
          >
            {this.displayDoctors()}
          </Card.Group>
        </Grid>
      </div>
    );
  }
}
