import React from 'react';
import About from './About';
import Reviews from './Reviews';
import Photos from './Photos';
import { Label, Menu, Tab, Icon } from 'semantic-ui-react';

const ShowMenu = ({ favorite, rate, doctor, reviews, photos }) => {
  const panes = [
    {
      menuItem: { key: 'about', icon: 'user', content: 'About' },
      render: () => (
        <Tab.Pane>
          <About favorite={favorite} rate={rate} doctor={doctor} />
        </Tab.Pane>
      ),
    },
    {
      menuItem: {
        key: 'reviews',
        icon: 'edit',
        content: 'Reviews',
      },
      render: () => (
        <Tab.Pane>
          {/* <Label>{reviews && reviews.length} */}
          <Reviews reviews={reviews} />
          {/* </Label> */}
        </Tab.Pane>
      ),
    },
    {
      menuItem: (
        <Menu.Item
          content='Photos'
          label={`${photos && photos.length}`}
          key='photos'
          icon='image'
        >
          Photos
          <Label inverted><Icon inverted name='image'></Icon>{photos && photos.length}</Label>
        </Menu.Item>
      ),
      render: () => (
        <Tab.Pane>
          <Photos photos={photos} />
        </Tab.Pane>
      ),
    },
  ];
  return <Tab menu={{ color: 'red', inverted: true }} panes={panes} />;
};

export default ShowMenu;
