import React from 'react';
import { Form, Header, Image, Button, Grid, Segment } from 'semantic-ui-react';
import axios from 'axios';


class Search extends React.Component {
  state = {
    address: '',
    query: '',
    distance: '',
    browserLocation: false,
    lat: null,
    lng: null,
  };

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();
    const formData = this.state;
    this.props.googleSearch(formData);
    this.props.loadingHandler();
  };

  useCurrentPosition = async (e) => {
    const {lat, lng} = this.state
    const geo = navigator.geolocation;

    let address = '';
    if (!geo) {
      console.log('Geo not supported');
    } else {
      geo.getCurrentPosition((position) => {
        this.setState({lat: position.coords.latitude, lng: position.coords.length}, 
          () => console.log(this.state));

      })
    }


    if(lat && lng){
      try {
        const {lat,lng} =  this.state

        address = await axios.get(
          `http://localhost:3000/doctors/getaddress/${lat}/${lng}`
        );

        address = address.results.formatted_address;
        this.setState({address})
      } catch (err) {
        console.error(err)
      }
    }
  };

  render() {
    return (
      <div>
        <Grid
          textAlign='center'
          style={{ height: '100vh' }}
          verticalAlign='middle'
        >
          <Grid.Column style={{ maxWidth: 900 }}>
            <Header as='h2' color='red' textAlign='center'>
              <Image src='../favicon.ico' />
              Telemed
            </Header>
            {this.props.error && (
              <Segment>No search results found / API is currently down</Segment>
            )}
            <Form onSubmit={this.onSubmit}>
              <>
                <Form.Field>
                  <label>Location</label>
                  <button onClick={this.useCurrentPosition}>
                    Use Current Location
                  </button>
                  <input
                    required
                    name='address'
                    onChange={(e) => this.onChange(e)}
                    type='text'
                    placeholder='Address'
                    value={this.state.address}
                  />
                </Form.Field>

                <Form.Field>
                  <label>What kind of doctor do you need to see?</label>
                  <input
                    required
                    name='query'
                    onChange={(e) => this.onChange(e)}
                    type='text'
                    placeholder='...foot, heart, etc'
                    value={this.state.query}
                  />
                </Form.Field>
                <Form.Field>
                  <label>How far are you willing to travel?</label>
                  <input
                    label={'Travel Distance'}
                    required
                    name='distance'
                    onChange={(e) => this.onChange(e)}
                    type='number'
                    placeholder='...in miles'
                    value={this.state.distance}
                  />
                </Form.Field>
                {!this.props.isLoading && (
                  <Button color='red' type='submit'>
                    Get Doctors
                  </Button>
                )}
                {this.props.isLoading && (
                  <Button loading color='red' type='submit'></Button>
                )}
              </>
            </Form>
          </Grid.Column>
        </Grid>
      </div>
    );
  }
}

export default Search;
