import React from 'react';
import axios from 'axios';

export const fetchApiDoctors = async (payload) => {
  let data;
  try {
    let res = await axios.post(`http://localhost:3000/doctors`, payload);
    debugger

    data = res.data.results;
  } catch (err) {
    console.log('This is your error:', err);
    throw err;
  }
  return data;
};

export const createDoctors = async (incomingDoctors, query) => {
  const dockers = [];
  await incomingDoctors.forEach(async (doctor) => {
    let finalDoc;
    let createdReviews
    let createdPhotos
    try {
      finalDoc = await createDoctor(doctor,query);

    } catch (err) {
      console.log(err);
      throw err;
    }
    try {

      createdReviews = await createReviews(finalDoc);
      finalDoc.reviews = createdReviews;

    } catch (error) {

    }  
    try {

      createdPhotos = await createPhotos(finalDoc);
      finalDoc.photos = createdPhotos;
    } catch (error) {

    }

    dockers.push(finalDoc);
  });

  return dockers;
};

const createDoctor = async (singleDoctor) => {
  let detailedDoctor;
  let doctorFromBackend;
  try {
    let res = await axios.get(
      `http://localhost:3000/geocodes/details/${singleDoctor.place_id}`
    );
    detailedDoctor = res.data.result;
  } catch (err) {
    console.log(err, 'FROM CREATEDOCTORS HELPER');
    throw err;
  }

  const {
    formatted_address,
    formatted_phone_number,
    name,
    place_id,
    rating,
    url,
    website,
    user_ratings_total,
    reviews,
    photos,
    geometry: {
      location: { lat, lng },
    },
  } = detailedDoctor;

  // ES6 key value assignment operator

  const doctorHash = {
    place_id,
    name,
    formatted_address,
    rating,
    user_ratings_total,
    formatted_phone_number,
    lat,
    lng,
    url,
    website,
    photos,
    reviews,
  };

  try {
    let res = await axios.post('http://localhost:3000/doctors', doctorHash);
    doctorFromBackend = res.data;



  } catch (error) {
    console.log('fixed?');
  }
  return doctorFromBackend

};

const createReviews = async ({place_id, doctor_id}) => {
  const reviewPayload = {
    place_id,
    doctor_id,
  };


  try {
    let res = await axios.post('http://localhost:3000/reviews', reviewPayload);
    return res.data;
  } catch (error) {
    console.log(error);
    throw error;
  }
};

const createPhotos = async (doctorFromBackend) => {
  const photoHash = {
    doctor_id: doctorFromBackend.id,
    doctor_reference: doctorFromBackend.place_id,
  };
  let data;
  try {
    let res = await axios.post(`http://localhost:3000/photos`, photoHash);
    data = res.data;
  } catch (err) {
    console.log(err);
    throw err;
  }
  return data;
};
