import React from 'react';
import PropTypes from 'prop-types';
import { Form, FormField, Label, Input, Button } from 'semantic-ui-react';
import { useEffect } from 'react';
import { useState } from 'react';


const Edit = ({ currentUser, loadUser, patchUser,loading }) => {
  const [formData, setFormData] = useState({});
  const [error, setError] = useState(true);
  useEffect(() => {
    resizeTo()
    setFormData({
      first_name: '',
      last_name:'',
      email: currentUser && currentUser.email,
      address:'',
      password: '',
      password2: '',
    });
  }, [currentUser]);

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });


  };

  const onSubmit = async (e) => {
    e.preventDefault();
    await patchUser(formData)

  };



  return (
    <Form onSubmit={onSubmit} display='flex'>
      <FormField error={error}>
        <Input
          label='First Name'
          placeholder={
            currentUser && currentUser.first_name
              ? currentUser.first_name
              : 'John'
          }
          labelPosition='left'
          type='text'
          value={formData.first_name}
          name='first_name'
          onChange={onChange}
        />
      </FormField>
      <FormField error={error}>
        <Input
          label='Last Name'
          placeholder={
            currentUser && currentUser.last_name ? currentUser.last_name : 'Doe  '
          }
          labelPosition='left'
          type='text'
          value={formData.last_name}
          name='last_name'
          onChange={onChange}
        />
      </FormField>
      <FormField error={error}>
        <Input
          label='Email'
          placeholder={
            currentUser && currentUser.email ? currentUser.email : 'Email...'
          }
          // labelPosition='left'
          type='email'
          value={formData.email}
          name='email'
          onChange={onChange}
        />
      </FormField>
      <FormField error={error}>
        <Input
          label='Address'
          placeholder={
            currentUser
              ? currentUser.address
                ? currentUser.address
                : '123 Main Street'
              : null
          }
          labelPosition='left'
          type='text'
          value={formData.address}
          name='address'
          onChange={onChange}
        />
      </FormField>
      <FormField error={error}>
        <Input
          label='Password'
          placeholder='Password...'
          labelPosition='left'
          type='password'
          value={formData.password}
          name='password'
          onChange={onChange}
        />
      </FormField>
      <FormField error={error}>
        <Input
          label='Password Confirmation'
          placeholder='Password Confirmation'
          labelPosition='left'
          type='password'
          value={formData.password2}
          name='password2'
          onChange={onChange}
        />
      </FormField>
      <Input
        color='red'
        type='submit'
        // as='button'
        // action={<Button  />}
        value='Submit'
      />
    </Form>
  );
};

Edit.propTypes = {
  loading: PropTypes.bool.isRequired,
  patchUser: PropTypes.func.isRequired,
  loadUser: PropTypes.func.isRequired,
  currentUser: PropTypes.object.isRequired,
  error: PropTypes.bool.isRequired,
};

export default Edit;
