import React from 'react';
import {
  Grid,
  Rating,
  Header,
  Message,
  Divider,
  Segment,
} from 'semantic-ui-react';

class DoctorShow extends React.Component {
  isFavorite = () => {
    // eslint-disable-next-line
    let favoriteArray = this.props.currentUser.doctors.filter(
      // eslint-disable-next-line
      (doctor) => doctor.id == this.props.match.params.id
    );
    if (favoriteArray.length > 0) {
      this.setState({ favorite: 1 });
    }
  };

  state = {
    favorite: 0,
    doc: {}
  };

  favoriteHandler = () => {
    this.setState({
      favorite: 0,
    });
  };

  docCheck = () => {
    let doc;
    if (this.props.doctors.length > 0) {
      // eslint-disable-next-line
      doc = this.props.doctors.find(
        // eslint-disable-next-line
        (doctor) => doctor.id == this.props.match.params.id
      );
      if (!!doc) {
        this.renderDocJsx(doc);
      }
    } else {
      doc = this.props.currentUser.doctors.find(
        // eslint-disable-next-line
        (doctor) => doctor.id == this.props.match.params.id
      );
      if (!!doc) {
        this.renderDocJsx(doc);
      }
    }
  };



  componentDidMount() {

  }

  rate = (e, data, doc) => {
    this.setState(
      {
        favorite: data.rating,
      },
      () => this.props.heart(doc)
    );
  };

  render() {
    console.log(this.props)
    // debugger
    const {name, formatted_phone_number,formatted_address, rating, url, website, reviews} = this.props.doctorShow
    // {
    //   "id": 11,
    //   "place_id": "ChIJsUsOo7px_ogRe0iGUoXl2kg",
    //   "name": "Heidi Williams, MD Plastic, Cosmetic, Reconstructive Surgery",
    //   "formatted_address": "999 Lake Hunter Cir unit d, Mt Pleasant, SC 29464, USA",
    //   "rating": 4.6,
    //   "user_ratings_total": null,
    //   "formatted_phone_number": "(843) 375-0270",
    //   "lat": 32.8145115,
    //   "lng": -79.862649,
    //   "url": "https://maps.google.com/?cid=5249760676431677563",
    //   "website": "http://www.heidiwilliamsplasticsurgery.com/",
    // }


    return (


      <div>
        <Grid
          columns={2}
          textAlign='center'
          style={{ height: '100vh' }}
          verticalAlign='middle'
        >
          <Grid.Row style={{ maxWidth: 1000 }}>
            <Grid.Column>
              <Segment>
                <Header>{name}</Header>
                <Rating
                  onRate={(e, data) => this.rate(e, data)}
                  icon='heart'
                  rating={this.state.favorite}
                  maxRating={1}
                  size='huge'
                />
                <Divider />
                <Message>Phone Number: {phone}</Message>
                <Message>Address: {vicinity}</Message>
                <Message>
                  <a
                    href={website}
                    rel='noopener noreferrer'
                    target='_blank'
                    to={website}
                  >
                    Website
                  </a>
                </Message>
                <Message>
                  <a href={url} rel=''>
                    Google Maps
                  </a>
                </Message>
                {rating && <Message>Average Rating: {rating}</Message>}
                {user_ratings_total && (
                  <Message>Total Ratings{user_ratings_total}</Message>
                )}
                {reviews &&
                  reviews.map((review) => (
                    <div key={review.author_name}>
                      <Message>
                        <span>
                          <strong>{review.author_name}</strong>
                        </span>
                        <p>{review.text}</p>
                      </Message>
                    </div>
                  ))}
              </Segment>
            </Grid.Column>
            <Grid.Column></Grid.Column>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default DoctorShow;
