import React from 'react';
import PropTypes from 'prop-types';
import { Form, FormField, Label, Input, Button } from 'semantic-ui-react';
import { useEffect } from 'react';
import { useState } from 'react';

const Edit = ({ currentUser, loadUser, patchUser }) => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    password2: '',
  });
  const [error, setError] = useState(true);
  useEffect(() => {
    loadUser();
  }, [loadUser]);

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });

    console.log(formData);
  };

  const onSubmit = async (e) => {
    e.preventDefault()
    console.log('got here in onSubmit')
  };

  const initialState = {};
  console.log(formData);
  return (
    <Form display='flex' >
      <FormField error={error}>
        <Input
          label='First Name'
          placeholder={
            currentUser && currentUser.firstName
              ? currentUser.firstName
              : 'John'
          }
          labelPosition='left'
          type='text'
          value={formData.firstName}
          name='firstName'
          onChange={onChange}
        />
      </FormField>
      <FormField error={error}>
        <Input
          label='Last Name'
          placeholder={
            currentUser && currentUser.lastName ? currentUser.lastName : 'Doe  '
          }
          labelPosition='left'
          type='text'
          value={formData.lastName}
          name='lastName'
          onChange={onChange}
        />
      </FormField>
      <FormField error={error}>
        <Input
          label='Email'
          placeholder={
            currentUser && currentUser.email ? currentUser.email : 'Email...'
          }
          // labelPosition='left'
          type='email'
          value={formData.email}
          name='email'
          onChange={onChange}
        />
      </FormField>
      <FormField error={error}>
        <Input
          label='Address'
          placeholder={
            currentUser
              ? currentUser.address
                ? currentUser.address
                : '123 Main Street'
              : null
          }
          labelPosition='left'
          type='text'
          value={formData.address}
          name='address'
          onChange={onChange}
        />
      </FormField>
      <FormField error={error}>
        <Input
          label='Password'
          placeholder='Password...'
          labelPosition='left'
          type='password'
          value={formData.password}
          name='password'
          onChange={onChange}
        />
      </FormField>
      <FormField error={error}>
        <Input
          label='Password Confirmation'
          placeholder='Password Confirmation'
          labelPosition='left'
          type='password'
          value={formData.password2}
          name='password2'
          onChange={onChange}
        />
      </FormField>
      <Button onClick={onSubmit} color='red' content='Edit'/>
    </Form>
  );
};

Edit.propTypes = {
  patchUser: PropTypes.func.isRequired,
  loadUser: PropTypes.func.isRequired,
  currentUser: PropTypes.object.isRequired,
  error: PropTypes.bool.isRequired,
};

export default Edit;
