import React, { Component } from 'react';

import { Form, FormField, Label, Input, Button } from 'semantic-ui-react';

export default class Edit extends Component {

  state={
    first_name: "",
    last_name: "",
    email: "",
    address: "",
    password: "",
    password2:""
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onSubmit = async (e) => {
    const formData = this.state
    e.preventDefault();
    await patchUser(formData);
  };

  render() {
    const { currentUser } = this.props.currentUser;
    console.log("PROPS FROM EDITJS",this.props)
    const {first_name, last_name, address, email, password, password2} = this.state

    return (
      <Form onSubmit={this.onSubmit} display='flex'>
        <FormField error={error}>
          <Input
            label='First Name'
            placeholder={
              currentUser && currentUser.first_name
                ? currentUser.first_name
                : 'John'
            }
            labelPosition='left'
            type='text'
            value={this.state.first_name}
            name='first_name'
            onChange={onChange}
          />
        </FormField>
        <FormField error={error}>
          <Input
            label='Last Name'
            placeholder={
              currentUser && currentUser.last_name
                ? currentUser.last_name
                : 'Doe  '
            }
            labelPosition='left'
            type='text'
            value={formData.last_name}
            name='last_name'
            onChange={onChange}
          />
        </FormField>
        <FormField error={error}>
          <Input
            label='Email'
            placeholder={
              currentUser && currentUser.email ? currentUser.email : 'Email...'
            }
            // labelPosition='left'
            type='email'
            value={formData.email}
            name='email'
            onChange={onChange}
          />
        </FormField>
        <FormField error={error}>
          <Input
            label='Address'
            placeholder={
              currentUser
                ? currentUser.address
                  ? currentUser.address
                  : '123 Main Street'
                : null
            }
            labelPosition='left'
            type='text'
            value={formData.address}
            name='address'
            onChange={onChange}
          />
        </FormField>
        <FormField error={error}>
          <Input
            label='Password'
            placeholder='Password...'
            labelPosition='left'
            type='password'
            value={formData.password}
            name='password'
            onChange={onChange}
          />
        </FormField>
        <FormField error={error}>
          <Input
            label='Password Confirmation'
            placeholder='Password Confirmation'
            labelPosition='left'
            type='password'
            value={formData.password2}
            name='password2'
            onChange={onChange}
          />
        </FormField>
        <Input
          color='red'
          type='submit'
          // as='button'
          // action={<Button  />}
          value='Submit'
        />
      </Form>
    );
  }
}
