import React from 'react';
import { Route, Switch, withRouter } from 'react-router-dom';
import { Button, Image } from 'semantic-ui-react';
import axios from 'axios';
import DoctorShow from './components/DoctorShow';
import Doctors from './containers/Doctors';
import Login from './components/Login';
import Profile from './components/Profile';
import Search from './components/Search';
import './App.css';

class App extends React.Component {
  state = {
    isLoading: false,
    isLoggedIn: false,
    register: false,
    favorite: 0,
    currentUser: {
      id: '',
      email: '',
      address: '',
      password: '',
      passwordConfirmation: '',
      firstName: '',
      lastName: '',
      doctors: [],
      favorites: [],
    },
    lat: 0,
    lng: 0,
    doctors: [],
    searchedPlaceIds: [],
    error: false,
    apiDoctors: [],
    users: [],
  };

  isFavorite = () => {
    let favoriteArray = this.state.currentUser.doctors.filter(
      // eslint-disable-next-line
      (doctor) => doctor.api_id == this.props.match.params.id
    );
    if (favoriteArray.length > 0) {
      this.setState({
        favorite: 1,
      });
    }
  };

  rate = (e, data) => {};

  heart = (doctor) => {
    let favorite = this.state.currentUser.favorites.find(
      // eslint-disable-next-line
      (favorite) => favorite.api_id == doctor.api_id
    );

    // eslint-disable-next-line
    if (typeof favorite == 'object') {
      this.unHeart(favorite);
    } else {
      this.favorite(doctor);
    }
  };

  unHeart = (favorite) => {
    //

    // let favorite = this.state.currentUser.userFavorites.find(favorite => favorite.api_id === doctor.uid)
    // eslint-disable-next-line
    fetch(`http://localhost:3000/favorites/${favorite.id}`, {
      method: 'DELETE',
    })
      .then((resp) => resp.json())
      .then((data) => {
        let favorites = this.state.currentUser.favorites.filter(
          (favorite) => favorite.id !== data.id
        );
        let doctors = this.state.currentUser.doctors.filter(
          (doctor) => doctor.api_id !== data.api_id
        );
        this.setState({
          ...this.state,
          favorite: 0,
          doctors: [data.doctor, ...this.state.doctors],
          currentUser: {
            ...this.state.currentUser,
            favorites: favorites,
            doctors: doctors,
          },
        });
      });
  };

  favorite = (doctor) => {
    let favoriteObject = {
      user_id: this.state.currentUser.id,
      doctor_id: doctor.id,
      api_id: doctor.api_id,
    };

    fetch('http://localhost:3000/favorites', {
      method: 'POST',
      headers: {
        'content-type': 'application/json',
        accepts: 'application/json',
      },
      body: JSON.stringify(favoriteObject),
    })
      .then((resp) => resp.json())
      .then((data) => {
        let favorites = [...this.state.currentUser.favorites];
        let doctors = [...this.state.currentUser.doctors];

        let newdoc = {
          id: data.doctor_id,
          api_id: doctor.api_id,
          first_name: doctor.first_name,
          last_name: doctor.last_name,
          title: doctor.title,
          gender: doctor.gender,
          bio: doctor.bio,
          phone_number: doctor.phone_number,
        };

        // userDoctors.filter
        this.setState({
          ...this.state,
          favorite: 1,
          currentUser: {
            ...this.state.currentUser,
            favorites: [data, ...favorites],
            doctors: [newdoc, ...doctors],
          },
        });
      });
  };

  onSubmit = (formData) => {
    this.logInOrSignUp(formData);
  };

  logInOrSignUp = (formData) => {
    fetch('http://localhost:3000/users', {
      method: 'POST',
      headers: {
        'content-type': 'application/json',
        accepts: 'application/json',
      },
      body: JSON.stringify({
        email: formData.email,
        password: formData.password,
        password_confirmation: formData.passwordConfirmation,
      }),
    })
      .then((resp) => resp.json())
      .then((data) => {
        this.setState(
          {
            ...this.state,
            currentUser: {
              id: data.id,
              email: data.email,
              address: data.address,
              password: data.password,
              passwordConfirmation: data.password_confirmation,
              firstName: data.first_name,
              lastName: data.last_name,
              doctors: data.doctors,
              favorites: data.favorites,
            },
            isLoggedIn: true,
          },
          () => this.props.history.push('/search')
        );
      });
  };

  userProfile = () => {
    let id = this.state.currentUser.id;
    fetch(`http://localhost:3000/users/${id}`)
      .then((resp) => resp.json())
      .then((data) => {
        this.setState(
          {
            ...this.state,
            currentUser: {
              ...this.state.currentUser,
              doctors: data.doctors,
            },
          },
          () => {
            this.props.history.push('/profile');
          }
        );
      });
  };

  logOut = () => {
    this.props.history.push('/');
    this.setState({ isLoggedIn: false, currentUser: {}, doctors: [] });
  };

  onSignUp = () => {
    this.setState({ register: !this.state.register });
  };
  // TODO retype this stuff
  // TODO validations for max distance

  createDoctors = async (incomingDoctors) => {

    return await incomingDoctors.map(async (doctor) => {
      let detailedDoctor;

      try {
        let res = await axios.get(
          `http://localhost:3000/geocodes/details/${doctor.place_id}`
        );
        detailedDoctor = res.data.result;
      } catch (err) {}

      // destructuring for values in schema
      const {
        formatted_address,
        formatted_phone_number,
        name,
        place_id,
        rating,
        url,
        website,
        user_ratings_total,
        reviews,
        photos,
        geometry: {
          location: { lat, lng },
        },
      } = detailedDoctor;

      // ES6 key value assignment operator

      const doctorHash = {
        place_id,
        name,
        formatted_address,
        rating,
        user_ratings_total,
        formatted_phone_number,
        lat,
        lng,
        url,
        website,
        photos,
        reviews,
      };

      // create post headers
      const myHeaders = new Headers();

      myHeaders.append('Content-Type', 'Application/json');
      myHeaders.append('accepts', 'application/json');

      // TODO check into stringify
      // TODO check into redirect: follow
      // options hash for post request to api

      const postDoctorsOptions = {
        method: 'POST',
        headers: myHeaders,
        body: JSON.stringify(doctorHash),
      };

      let data;

      // CREATE DOCTOR

      try {
        let res = await fetch(
          'http://localhost:3000/doctors',
          postDoctorsOptions
        );

        data = await res.json();



        let reviewData;
        await reviews.forEach(async (singleReview) => {
          const {
            author_name,
            author_url,
            profile_photo_url,
            rating,
            relative_time_description,
            text,
            time,
          } = singleReview;
          const reviewHash = {
            author_name,
            author_url,
            profile_photo_url,
            rating,
            relative_time_description,
            text,
            time,
            place_id,
            doctor_id: data.id,
          };
          const postReviewOptions = {
            method: 'POST',
            headers: myHeaders,
            body: JSON.stringify(reviewHash),
          };
          let res = await fetch(
            'http://localhost:3000/reviews',
            postReviewOptions
          );

          reviewData = await res.json();


          //
        });

        const photoPayload = {
          incomingPhotos: photos,
          doctorId: data.id,
          placeId: place_id,
        };



        return await this.createPhotos(photoPayload);
        // await this.fetchDoctors(doctorIds)

        // TODO actually need to be setting state with reviews and photos properly added in
        // this.setState({
        //   doctors: [...this.state.doctors, data],
        // });
      } catch (err) {
        console.log(err);
      }
    });
  };

  fetchDoctors = async () => {
    let res =  axios.get(`http://localhost:3000/doctors`)




  }

