    const myHeaders = new Headers();

    myHeaders.append('Content-Type', 'Application/json');
    myHeaders.append('accepts', 'application/json');

    const photoHash = {
      doctor_id: doctorId,
      doctor_reference: placeId,
    };
    let data;
    try {
      const postPhotoOptions = {
        method: 'POST',
        headers: myHeaders,
        body: JSON.stringify(photoHash),
        redirect: 'follow',
      };

      let res = await fetch(`http://localhost:3000/photos`, postPhotoOptions);
      data = await res.json();
      console.log(data.photos)
      // return data



    } catch (err) {
      console.log(err);
    }
  };



  googleSearch = async (payload) => {

    const { address, query, distance, lat, lng } = payload;
    let data;
    try {
      let res = await axios.get(
        `http://localhost:3000/geocodes/nearby/?address=${address}&query=${query}&distance=${distance}&lat=${lat}&lng=${lng}`
      );

      data = res.data.results;
      const searchedPlaceIds = data.map(place => place.place_id )



      await this.createDoctors(data)
      this.setState({searchedPlaceIds})



    } catch (err) {
