    } else {
      this.setState({ error: false });
    }
  };

  loadingHandler = () => {
    this.setState({ isLoading: true });
  };

  patchUser = (userData) => {
    fetch(`http://localhost:3000/users/${this.state.currentUser.id}`, {
      method: 'PATCH',
      headers: {
        'content-type': 'application/json',
        accepts: 'application/json',
      },
      body: JSON.stringify({
        email: userData.email,
        password: userData.password,
        password_confirmation: userData.passwordConfirmation,
        first_name: userData.firstName,
        last_name: userData.lastName,
        address: userData.address,
      }),
    })
      .then((resp) => resp.json())
      .then((data) =>
        this.setState({
          ...this.state,
          currentUser: {
            doctors: data.doctors,
            favorites: data.favorites,
            id: data.id,
            email: data.email,
            address: data.address,
            password: data.password,
            passwordConfirmation: data.password_confirmation,
            firstName: data.first_name,
            lastName: data.last_name,
          },
        })
      );
  };



  componentDidMount() {
    // let doctorsInCurrentState = [...this.state.doctors]
    fetch(`http://localhost:3000/users`)
      .then((resp) => resp.json())
      .then((users) => {
        this.setState({
          users,
        });
      });
  }

  searchButton = () => {
    if (this.state.isLoggedIn) {
      this.props.history.push('/search');
    } else {
      alert('Please log in before searching');
    }
  };

  render() {
    // console.log('this.state from App.js: ', this.state);
    return (
      <div>
        <Button color='red' onClick={() => this.searchButton()}>
          Search
        </Button>
        {this.state.isLoggedIn && (
          <Button color='red' onClick={() => this.userProfile()}>
            Profile
          </Button>
        )}
        {this.state.isLoggedIn && (
          <Button color='red' onClick={this.logOut}>
            Logout
          </Button>
        )}
        <Image alt='' src=''></Image>
        {!this.state.isLoggedIn && (
          <Login
            isLoggedIn={this.state.isLoggedIn}
            onSignUp={this.onSignUp}
            onSubmit={this.onSubmit}
            register={this.state.register}
          />
        )}
        <Switch>
          <Route
            exact
            path='/doctors'
            render={(routerProps) => (
              <Doctors {...routerProps} doctors={this.state.doctors} />
            )}
          />
          <Route
            exact
            path='/search'
            render={(routerProps) => (
              <Search
                error={this.state.error}
                isLoading={this.state.isLoading}
                currentUser={this.state.currentUser}
                loadingHandler={this.loadingHandler}
                {...routerProps}
                googleSearch={this.googleSearch}
                favorite={this.favorite}
              />
            )}
          />
          <Route
            exact
            path='/doctors/:id'
            render={(routerProps) => (
              <DoctorShow
                rate={this.rate}
                doctors={this.state.doctors}
                favorite={this.state.favorite}
                isFavorite={this.isFavorite}
                heart={this.heart}
                currentUser={this.state.currentUser}
                {...routerProps}
                // eslint-disable-next-line
                favorite={this.favorite}
              />
            )}
          />
          <Route
            exact
            path='/profile'
            render={(routerProps) => (
              <Profile
                patchUser={this.patchUser}
                currentUser={this.state.currentUser}
                doctors={this.state.doctors}
                isLoggedIn={this.state.isLoggedIn}
                {...routerProps}
              />
            )}
          />
        </Switch>
      </div>
    );
  }
}

export default withRouter(App);
