import React from 'react';
import PropTypes from 'prop-types';
import { Form, FormField, Label } from 'semantic-ui-react';

const Edit = ({ patchUser, loadUser, currentUser }) => {
  useEffect(() => {
    loadUser();
  }, [loadUser]);

  const onChange = (e) =>
  setFormData({ ...formData, [e.target.name]: e.target.value });

  const [editForm, seteditForm] = useState({currentUser:{firstName, lastName, email, address, password}})
  return (
    <Form onSubmit={patchUser}>
      <FormField>
        <Input value={firstName} name='first' onChange={onChange}></Input>
      </FormField>
    </Form>
  );
};

Edit.propTypes = {
  submitEdit: PropTypes.func.isRequired,
};

export default Edit;
