import React from 'react';
import axios from 'axios';

export const fetchApiDoctors = async (payload) => {
  const { address, query, distance, lat, lng } = payload;
  let data;
  try {
    let res = await axios.get(
      `http://localhost:3000/geocodes/nearby/?address=${address}&query=${query}&distance=${distance}&lat=${lat}&lng=${lng}`
    );

    data = res.data.results;
  } catch (err) {
    console.log('This is your error:', err);
    throw err;
  }
  return data;
};

export const createDoctors = async (incomingDoctors) => {
  await incomingDoctors.forEach(async (doctor) => {
    try {
      finalDoc = await createDoctor(doctor);
      return finalDoc.data;
    } catch (err) {
      console.log(err);
      throw err;
    }
  });
};

const createDoctor = async (singleDoctor) => {
  let detailedDoctor;
  let doctorFromBackend;
  try {
    let res = await axios.get(
      `http://localhost:3000/geocodes/details/${singleDoctor.place_id}`
    );
    detailedDoctor = res.data.result;
  } catch (err) {
    console.log(err, 'FROM CREATEDOCTORS HELPER');
    throw err;
  }

  const {
    formatted_address,
    formatted_phone_number,
    name,
    place_id,
    rating,
    url,
    website,
    user_ratings_total,
    reviews,
    photos,
    geometry: {
      location: { lat, lng },
    },
  } = detailedDoctor;

  // ES6 key value assignment operator

  const doctorHash = {
    place_id,
    name,
    formatted_address,
    rating,
    user_ratings_total,
    formatted_phone_number,
    lat,
    lng,
    url,
    website,
    photos,
    reviews,
  };

  try {
    let res = await axios.post('http://localhost:3000/doctors', doctorHash);
    doctorFromBackend = res.data;
    debugger;
    let createdReviews = await createReviews(
      doctorFromBackend,
      doctorHash.reviews
    );
    doctorFromBackend.reviews = createdReviews;
    let createdPhotos = await createPhotos(doctorFromBackend);
    doctorFromBackend.photos = createdPhotos;
  } catch (error) {}

  return doctorFromBackend;
};

const createReviews = async (doctorFromBackend, reviews) => {
  const revs = [];
  reviews.forEach(async (singleReview) => {
    revs.push(await createReview(singleReview, doctorFromBackend));
  });
  return revs;
};

const createReview = async (singleReview, doctorFromBackend) => {
  const {
    author_name,
    author_url,
    profile_photo_url,
    rating,
    relative_time_description,
    text,
    time,
  } = singleReview;
  const reviewHash = {
    author_name,
    author_url,
    profile_photo_url,
    rating,
    relative_time_description,
    text,
    time,
    place_id: doctorFromBackend.place_id,
    doctor_id: doctorFromBackend.id,
  };

  try {
    let res = await axios.post('http://localhost:3000/reviews', reviewHash);
    return res.data;
  } catch (error) {
    console.log(error);
    throw error;
  }
};

const createPhotos = async ({ doctorFromBackend }) => {
  let data = [];

  const photoHash = {
    doctor_id: doctorFromBackend.id,
    doctor_reference: doctorFromBackend.place_id,
  };

  try {
    let res = await axios.post(`http://localhost:3000/photos`, photoHash);

    data = res.data;
  } catch (err) {
    console.log(err);
    throw err;
  }
  return data;
};
