import React from 'react';
import PropTypes from 'prop-types';
import { Form, FormField, Label, Input } from 'semantic-ui-react';
import { useEffect } from 'react';
import { useState } from 'react';

const Edit = (props) => {
  const {currentUser, loadUser, patchUser} = props
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    password2: ''
  });
  useEffect(() => {
    loadUser();
  }, [loadUser]);

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });

    console.log(formData);
  };

  const onSubmit = async () => {};

  const initialState = {};
  console.log(formData)
  return (
    <Form onSubmit={onSubmit}>
      <FormField >
        <Input

        label='First Name'
        placeholder={currentUser ? currentUser.firstName : "John"}
        labelPosition='left'
        type='text'
          value={formData.firstName}
          name='firstName'
          onChange={(e) => onChange(e)}
        ></Input>
      </FormField>
      <FormField >
        <Input

        label='Last Name'
        placeholder={currentUser ? currentUser.lastName : "Doe"}
        labelPosition='left'
        type='text'
          value={formData.lastName}
          name='lastName'
          onChange={(e) => onChange(e)}
        ></Input>
      </FormField>
      <FormField >
        <Input

        label='Address'
        placeholder={currentUser ? currentUser.address : "John"}
        labelPosition='left'
        type='text'
          value={formData.address}
          name='address'
          onChange={(e) => onChange(e)}
        ></Input>
      </FormField>
      <FormField >
        <Input

        label='First Name'
        placeholder={currentUser ? currentUser.email : "John"}
        labelPosition='left'
        type='email'
          value={formData.email}
          name='email'
          onChange={(e) => onChange(e)}
        ></Input>
      </FormField>
      <FormField >
        <Input

        label='Password'
        placeholder={currentUser ? currentUser.firstName : "John"}
        labelPosition='left'
        type='password'
          value={formData.firstName}
          name='firstName'
          onChange={(e) => onChange(e)}
        ></Input>
      </FormField>
    </Form>
  );
};

Edit.propTypes = {
  patchUser: PropTypes.func.isRequired,
  loadUser: PropTypes.func.isRequired,
  currentUser: PropTypes.object.isRequired,
};

export default Edit;
