import React from 'react';
import { Route, Switch, withRouter } from 'react-router-dom';
import { Button, Image } from 'semantic-ui-react';
import axios from 'axios';
import DoctorShow from './components/DoctorShow';
import Doctors from './containers/Doctors';
import Login from './components/Login';
import Profile from './components/Profile';
import Search from './components/Search';
import './App.css';
// import { createDoctors } from './components/helpers/helpers';

class App extends React.Component {
  state = {
    isLoading: false,
    isLoggedIn: false,
    register: false,
    favorite: 0,
    currentUser: {
      id: '',
      email: '',
      address: '',
      password: '',
      passwordConfirmation: '',
      firstName: '',
      lastName: '',
      doctors: [],
      favorites: [],
    },
    lat: 0,
    lng: 0,
    doctors: [],
    error: false,
    apiDoctors: [],
    users: [],
  };

  isFavorite = () => {
    let favoriteArray = this.state.currentUser.doctors.filter(
      // eslint-disable-next-line
      (doctor) => doctor.api_id == this.props.match.params.id
    );
    if (favoriteArray.length > 0) {
      this.setState({
        favorite: 1,
      });
    }
  };

  rate = (e, data) => {};

  heart = (doctor) => {
    let favorite = this.state.currentUser.favorites.find(
      // eslint-disable-next-line
      (favorite) => favorite.api_id == doctor.api_id
    );

    // eslint-disable-next-line
    if (typeof favorite == 'object') {
      this.unHeart(favorite);
    } else {
      this.favorite(doctor);
    }
  };

  unHeart = (favorite) => {
    // debugger

    // let favorite = this.state.currentUser.userFavorites.find(favorite => favorite.api_id === doctor.uid)
    // eslint-disable-next-line
    fetch(`http://localhost:3000/favorites/${favorite.id}`, {
      method: 'DELETE',
    })
      .then((resp) => resp.json())
      .then((data) => {
        let favorites = this.state.currentUser.favorites.filter(
          (favorite) => favorite.id !== data.id
        );
        let doctors = this.state.currentUser.doctors.filter(
          (doctor) => doctor.api_id !== data.api_id
        );
        this.setState({
          ...this.state,
          favorite: 0,
          doctors: [data.doctor, ...this.state.doctors],
          currentUser: {
            ...this.state.currentUser,
            favorites: favorites,
            doctors: doctors,
          },
        });
      });
  };

  favorite = (doctor) => {
    let favoriteObject = {
      user_id: this.state.currentUser.id,
      doctor_id: doctor.id,
      api_id: doctor.api_id,
    };

    fetch('http://localhost:3000/favorites', {
      method: 'POST',
      headers: {
        'content-type': 'application/json',
        accepts: 'application/json',
      },
      body: JSON.stringify(favoriteObject),
    })
      .then((resp) => resp.json())
      .then((data) => {
        let favorites = [...this.state.currentUser.favorites];
        let doctors = [...this.state.currentUser.doctors];

        let newdoc = {
          id: data.doctor_id,
          api_id: doctor.api_id,
          first_name: doctor.first_name,
          last_name: doctor.last_name,
          title: doctor.title,
          gender: doctor.gender,
          bio: doctor.bio,
          phone_number: doctor.phone_number,
        };

        // userDoctors.filter
        this.setState({
          ...this.state,
          favorite: 1,
          currentUser: {
            ...this.state.currentUser,
            favorites: [data, ...favorites],
            doctors: [newdoc, ...doctors],
          },
        });
      });
  };

  onSubmit = (formData) => {
    this.logInOrSignUp(formData);
  };

  logInOrSignUp = (formData) => {
    fetch('http://localhost:3000/users', {
      method: 'POST',
      headers: {
        'content-type': 'application/json',
        accepts: 'application/json',
      },
      body: JSON.stringify({
        email: formData.email,
        password: formData.password,
        password_confirmation: formData.passwordConfirmation,
      }),
    })
      .then((resp) => resp.json())
      .then((data) => {
        this.setState(
          {
            ...this.state,
            currentUser: {
              id: data.id,
              email: data.email,
              address: data.address,
              password: data.password,
              passwordConfirmation: data.password_confirmation,
              firstName: data.first_name,
              lastName: data.last_name,
              doctors: data.doctors,
              favorites: data.favorites,
            },
            isLoggedIn: true,
          },
          () => this.props.history.push('/search')
        );
      });
  };

  userProfile = () => {
    let id = this.state.currentUser.id;
    fetch(`http://localhost:3000/users/${id}`)
      .then((resp) => resp.json())
      .then((data) => {
        this.setState(
          {
            ...this.state,
            currentUser: {
              ...this.state.currentUser,
              doctors: data.doctors,
            },
          },
          () => {
            this.props.history.push('/profile');
          }
        );
      });
  };

  logOut = () => {
    this.props.history.push('/');
    this.setState({ isLoggedIn: false, currentUser: {}, doctors: [] });
  };

  onSignUp = () => {
    this.setState({ register: !this.state.register });
  };
  // TODO retype this stuff
  // TODO validations for max distance

  createDoctors = async (incomingDoctors) => {
    const newDoctors = await incomingDoctors.map(async (doctor) => {
      let detailedDoctor;
      try {
        let res = await axios.get(
          `http://localhost:3000/geocodes/details/${doctor.place_id}`
        );
        detailedDoctor = res.data.result;
      } catch (err) {
        console.log('Error', err);
      }

      // destructuring for values in schema
      const {
        formatted_address,
        formatted_phone_number,
        name,
        place_id,
        rating,
        url,
        website,
        user_ratings_total,
        reviews,
        photos,
        geometry: {
          location: { lat, lng },
        },
      } = detailedDoctor;

      // ES6 key value assignment operator

      const doctorHash = {
        place_id,
        name,
        formatted_address,
        rating,
        user_ratings_total,
        formatted_phone_number,
        lat,
        lng,
        url,
        website,
      };

      // create post headers
      const myHeaders = new Headers();

      myHeaders.append('Content-Type', 'Application/json');
      myHeaders.append('accepts', 'application/json');

      // TODO check into stringify
      // TODO check into redirect: follow
      // options hash for post request to api

      const postDoctorsOptions = {
        method: 'POST',
        headers: myHeaders,
        body: JSON.stringify(doctorHash),
        redirect: 'follow',
      };

      let data;
      try {
        let res = await fetch(
          'http://localhost:3000/doctors',
          postDoctorsOptions
        );

        data = await res.json();
        const reviewPayload = {
          doctorId: data.id,
          incomingReviews: reviews,
          place_id,
        };

        const photoPayload = {
          incomingPhotos: photos,
          doctorId: data.id

        }
        await this.createReviews(reviewPayload);
        await this.createPhotos(photoPayload)
        // TODO actually need to be setting state with reviews and photos properly added in
        // this.setState({
        //   doctors: [...this.state.doctors, data],
        // });
        // console.log("DATA FROM APP:", data);
      } catch (err) {
        console.log(err);
      }
    });
  };

  createPhotos = async ({incomingPhotos, doctorId}) => {

  }

  createReviews = async ({ incomingReviews, doctorId, place_id }) => {
    const myHeaders = new Headers();

    myHeaders.append('Content-Type', 'Application/json');
    myHeaders.append('accepts', 'application/json');

    // TODO check into stringify
    // TODO check into redirect: follow
    // options hash for post request to api




    incomingReviews.forEach(
      async ({
        author_name,
        author_url,
        profile_photo_url,
        rating,
        relative_time_description,
        text,
        time,
      }) => {
        let data
        try {
          let reviewHash = {
            doctor_id: doctorId,
            place_id,
            author_name,
            author_url,
            profile_photo_url,
            rating,
            relative_time_description,
            text,
            time
          }
          const postReviewOptions = {
            method: 'POST',
            headers: myHeaders,
            body: JSON.stringify(reviewHash),
            redirect: 'follow',
          };
          let res = await fetch(`http://localhost:3000/reviews`, postReviewOptions)
          data = await res.json()



        } catch (err) {
          console.log(err)
        }

      }
    );
  };

  googleSearch = async (payload) => {
    const { address, query, distance, lat, lng } = payload;

    try {
      let res = await axios.get(
        `http://localhost:3000/geocodes/nearby/?address=${address}&query=${query}&distance=${distance}&lat=${lat}&lng=${lng}`
      );

      res = res.data.results;
      await this.createDoctors(res);
    } catch (err) {
      console.log('This is your error:', err);
    }
  };

  isResolved = () => {
    if (this.state.apiDoctors.length < 1) {
      this.setState({ error: true });
    } else {
      this.setState({ error: false });
    }
  };

  loadingHandler = () => {
    this.setState({ isLoading: true });
  };

  patchUser = (userData) => {
    fetch(`http://localhost:3000/users/${this.state.currentUser.id}`, {
      method: 'PATCH',
      headers: {
        'content-type': 'application/json',
        accepts: 'application/json',
      },
      body: JSON.stringify({
        email: userData.email,
        password: userData.password,
        password_confirmation: userData.passwordConfirmation,
        first_name: userData.firstName,
        last_name: userData.lastName,
        address: userData.address,
      }),
    })
      .then((resp) => resp.json())
      .then((data) =>
        this.setState({
          ...this.state,
          currentUser: {
            doctors: data.doctors,
            favorites: data.favorites,
            id: data.id,
            email: data.email,
            address: data.address,
            password: data.password,
            passwordConfirmation: data.password_confirmation,
            firstName: data.first_name,
            lastName: data.last_name,
          },
        })
      );
  };

  componentDidMount() {
    // let doctorsInCurrentState = [...this.state.doctors]
    fetch(`http://localhost:3000/users`)
      .then((resp) => resp.json())
      .then((users) => {
        this.setState({
          users,
        });
      });
  }

  searchButton = () => {
    if (this.state.isLoggedIn) {
      this.props.history.push('/search');
    } else {
      alert('Please log in before searching');
    }
  };

  render() {
    // console.log('this.state from App.js: ', this.state);
    return (
      <div>
        <Button color='red' onClick={() => this.searchButton()}>
          Search
        </Button>
        {this.state.isLoggedIn && (
          <Button color='red' onClick={() => this.userProfile()}>
            Profile
          </Button>
        )}
        {this.state.isLoggedIn && (
          <Button color='red' onClick={this.logOut}>
            Logout
          </Button>
        )}
        <Image alt='' src=''></Image>
        {!this.state.isLoggedIn && (
          <Login
            isLoggedIn={this.state.isLoggedIn}
            onSignUp={this.onSignUp}
            onSubmit={this.onSubmit}
            register={this.state.register}
          />
        )}
        <Switch>
          <Route
            exact
            path='/doctors'
            render={(routerProps) => (
              <Doctors {...routerProps} doctors={this.state.doctors} />
            )}
          />
          <Route
            exact
            path='/search'
            render={(routerProps) => (
              <Search
                error={this.state.error}
                isLoading={this.state.isLoading}
                currentUser={this.state.currentUser}
                loadingHandler={this.loadingHandler}
                {...routerProps}
                googleSearch={this.googleSearch}
                favorite={this.favorite}
              />
            )}
          />
          <Route
            exact
            path='/doctors/:id'
            render={(routerProps) => (
              <DoctorShow
                rate={this.rate}
                doctors={this.state.doctors}
                favorite={this.state.favorite}
                isFavorite={this.isFavorite}
                heart={this.heart}
                currentUser={this.state.currentUser}
                {...routerProps}
                // eslint-disable-next-line
                favorite={this.favorite}
              />
            )}
          />
          <Route
            exact
            path='/profile'
            render={(routerProps) => (
              <Profile
                patchUser={this.patchUser}
                currentUser={this.state.currentUser}
                doctors={this.state.doctors}
                isLoggedIn={this.state.isLoggedIn}
                {...routerProps}
              />
            )}
          />
        </Switch>
      </div>
    );
  }
}

export default withRouter(App);
