import React from 'react';
import {
  Grid,
  Rating,
  Header,
  Message,
  Divider,
  Segment,
  Image,
} from 'semantic-ui-react';
import axios from 'axios';

class DoctorShow extends React.Component {

  state = {
    favorite: 0,
    currentUser: null,
    doc: {},
    id: null,
  };

  favoriteHandler = () => {
    this.setState({
      favorite: 0,
    });
  };

  async componentDidMount() {
    await this.props.getDoctorById(parseInt(this.props.match.params.id));
    if(this.props.currentUser.doctors.includes(this.props.doctorShow)){}
    const token = localStorage.token;
    if (token) {
      //get user info

      try {
        let res = await axios.get('http://localhost:3000/auto_login', {
          headers: { Authorization: token },
        });
        if(res.errors){
          this.setState({currentUser: null})
        } else {
          debugger
          this.setState({ currentUser: res.data });
        }

      } catch (error) {
        this.setState({currentUser: null})
        alert(error);
      }
    }
  }

  postFavorite = async () => {
    const favoriteObject = {
      doctor_id: this.props.match.params.id,
      user_id: this.state.currentUser.id
    }
    try {
      let res = await axios.post(`http://localhost:3000/favorites`, favoriteObject)
      this.setState({favorite: 1, id: res.data.id})
      console.log(res.data)
    } catch (error) {

    }
  }

  deleteFavorite = async () => {
    try {      
      let res = await axios.delete(`http://localhost:3000/favorites/${this.state.id}`)
      console.log("res.data from deletefavorite",res.data)
      this.setState({favorite: 0, id: null})
    } catch (err) {
      console.log("error", err)
    }

    console.log('got here in delete')
  }

  rate = (e, { rating }) => {
    if(rating === 0){
      this.deleteFavorite()
    } else {
      this.postFavorite()
    }


  };

  render() {
    console.log("DOCTOR SHOW THIS.STATE: ",this.state)
    // debugger
    const {
      name,
      formatted_phone_number,
      formatted_address,
      rating,
      url,
      website,
      reviews,
      photos,
    } = this.props.doctorShow;

    return (
      <div>
        <Grid
          columns={2}
          textAlign='center'
          style={{ height: '100vh' }}
          verticalAlign='middle'
        >
          <Grid.Row style={{ maxWidth: 1000 }}>
            <Grid.Column>
              <Segment>
                <Header>{name}</Header>
                <Rating
                  onRate={(e, data) => this.rate(e, data)}
                  icon='heart'
                  rating={this.state.favorite}
                  maxRating={1}
                  size='huge'
                />
                <Divider />
                <Message>Phone Number: {formatted_phone_number}</Message>
                <Message>Address: {formatted_address}</Message>
                <Message>
                  <a
                    href={website}
                    rel='noopener noreferrer'
                    target='_blank'
                    to={website}
                  >
                    Website
                  </a>
                </Message>
                <Message>
                  <a href={url} rel=''>
                    Google Maps
                  </a>
                </Message>
                {rating && <Message>Average Rating: {rating}</Message>}
                {reviews &&
                  reviews.map((review) => (
                    <div key={review.author_name}>
                      <Message>
                        <span>
                          <strong>{review.author_name}</strong>
                        </span>
                        <p>{review.text}</p>
                      </Message>
                    </div>
                  ))}
              </Segment>
            </Grid.Column>
            <Grid.Column>
              {photos &&
                photos.map((photo) => (
                  <Image key={photo.id} alt='' src={photo.slug}></Image>
                ))}
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default DoctorShow;
