import React from 'react';
import {
  Grid,
  Rating,
  Header,
  Message,
  Divider,
  Segment,
  Image,
} from 'semantic-ui-react';

class DoctorShow extends React.Component {
  isFavorite = () => {
    // eslint-disable-next-line
    let favoriteArray = this.props.currentUser.doctors.filter(
      // eslint-disable-next-line
      (doctor) => doctor.id == this.props.match.params.id
    );
    if (favoriteArray.length > 0) {
      this.setState({ favorite: 1 });
    }
  };

  state = {
    favorite: 0,
    doc: {},
  };

  favoriteHandler = () => {
    this.setState({
      favorite: 0,
    });
  };

  componentDidMount() {
    this.props.getDoctorById(parseInt(this.props.match.params.id));
  }

  rate = (e, { rating }) => {
    console.log(rating, this.props.currentUser);
    this.setState(
      {
        favorite: rating,
      },
      () => this.props.heart(doc)
    );
  };

  render() {
    // debugger
    const {
      name,
      formatted_phone_number,
      formatted_address,
      rating,
      url,
      website,
      reviews,
      photos,
    } = this.props.doctorShow;

    return (
      <div>
        <Grid
          columns={2}
          textAlign='center'
          style={{ height: '100vh' }}
          verticalAlign='middle'
        >
          <Grid.Row style={{ maxWidth: 1000 }}>
            <Grid.Column>
              <Segment>
                <Header>{name}</Header>
                <Rating
                  onRate={(e, data) => this.rate(e, data)}
                  icon='heart'
                  rating={this.state.favorite}
                  maxRating={1}
                  size='huge'
                />
                <Divider />
                <Message>Phone Number: {formatted_phone_number}</Message>
                <Message>Address: {formatted_address}</Message>
                <Message>
                  <a
                    href={website}
                    rel='noopener noreferrer'
                    target='_blank'
                    to={website}
                  >
                    Website
                  </a>
                </Message>
                <Message>
                  <a href={url} rel=''>
                    Google Maps
                  </a>
                </Message>
                {rating && <Message>Average Rating: {rating}</Message>}
                {reviews &&
                  reviews.map((review) => (
                    <div key={review.author_name}>
                      <Message>
                        <span>
                          <strong>{review.author_name}</strong>
                        </span>
                        <p>{review.text}</p>
                      </Message>
                    </div>
                  ))}
              </Segment>
            </Grid.Column>
            <Grid.Column>
              {photos &&
                photos.map((photo) => (
                  <Image key={photo.id} alt='' src={photo.slug}></Image>
                ))}
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default DoctorShow;
