import React from 'react';

import {
  Header,
  Divider,
  Button,
  Form,
  Input,
  Grid,
  Card,
  Icon,
} from 'semantic-ui-react';

import DoctorCard from '../DoctorCard';
import PropTypes from 'prop-types';


const Profile = ({ currentUser, loadUser, history,loading }) => {
  const profileDoctors = () =>
    currentUser.doctors
      ? currentUser.doctors.map((doc) => (
          <DoctorCard key={doc.id} doctor={doc} />
        ))
      : history.push('/login');
  // useEffect(() => {
  //   // loadUser();
  //   profileDoctors();
  // }, [ profileDoctors]);

  return (
    <>
      <Grid style={{ padding: '2em' }} columns={2}>
        <Grid.Row>
          {currentUser && (
            <>
              <Header display='flex' textAlign='center' color='red'>
                <Icon size='tiny' name='edit'></Icon>
                {currentUser.email}
              </Header>
            </>
          )}
          <Divider />
        </Grid.Row>
        {currentUser && (
          <Grid.Row>
            <Header as='h2' display='flex' textAlign='center' color='red'>
              Favorite Doctors
            </Header>
            <Divider />
            <Card.Group
              style={{ overflow: 'auto', maxHeight: '52em' }}
              display='flex'
              justify-content='center'
              itemsPerRow={1}
            >
              {profileDoctors()}
            </Card.Group>
          </Grid.Row>
        )}
      </Grid>
    </>
  );
};

Profile.propTypes = {
  loadUser: PropTypes.func.isRequired,
  currentUser: PropTypes.object.isRequired,
};

export default Profile;
