import React from 'react';
import { Card, Grid } from 'semantic-ui-react';
import DoctorCard from '../components/DoctorCard';


class Doctors extends React.Component {

  displayDoctors = () => {
    if (this.props.loading) {
      return <h1>Loading</h1>;
    } else {
      return this.props.doctors.map((doctor) => {

          return <DoctorCard currentUser={this.props.currentUser} getDoctorById={this.props.getDoctorById}  key={doctor.id} doctor={doctor} />

      });
    }
  };

  componentDidMount(){
    this.props.getDoctors()
  }

  render() {
    return (
      <div>
        <Grid
          textAlign='center'
          style={{ height: '100vh' }}
          verticalAlign='middle'
        >
          <br></br>
          <Card.Group
            style={{ overflow: 'auto', maxHeight: '65em' }}
            display='flex'
            justify-content='center'
            itemsPerRow={6}
          >
            {this.displayDoctors()}
          </Card.Group>
        </Grid>
      </div>
    );
  }
}

export default Doctors;
