import React, { Component } from 'react';

import { Form, FormField, Label, Input, Button } from 'semantic-ui-react';

export default class Edit extends Component {
  state = {
    first_name: '',
    last_name: '',
    email: '',
    address: '',
    password: '',
    password2: '',
  };

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onSubmit = async (e) => {
    const formData = this.state;
    e.preventDefault();
    await this.props.patchUser(formData);
  };

  render() {
    if(this.props.currentUser)

    console.log('PROPS FROM EDITJS', this.props);
    const {
      first_name,
      last_name,
      address,
      email,
      password,
      password2,
    } = this.state;

    return (
      <Form onSubmit={this.onSubmit} display='flex'>
        <FormField>
          <Input
            label='First Name'
            placeholder={
              currentUser && currentUser.first_name
                ? currentUser.first_name
                : 'John'
            }
            labelPosition='left'
            type='text'
            value={this.state.first_name}
            name='first_name'
            onChange={this.onChange}
          />
        </FormField>
        <FormField>
          <Input
            label='Last Name'
            placeholder={
              currentUser && currentUser.last_name
                ? currentUser.last_name
                : 'Doe  '
            }
            labelPosition='left'
            type='text'
            value={this.state.last_name}
            name='last_name'
            onChange={this.onChange}
          />
        </FormField>
        <FormField>
          <Input
            label='Email'
            placeholder={
              currentUser && currentUser.email ? currentUser.email : 'Email...'
            }
            // labelPosition='left'
            type='email'
            value={this.state.email}
            name='email'
            onChange={this.onChange}
          />
        </FormField>
        <FormField>
          <Input
            label='Address'
            placeholder={
              currentUser
                ? currentUser.address
                  ? currentUser.address
                  : '123 Main Street'
                : null
            }
            labelPosition='left'
            type='text'
            value={this.state.address}
            name='address'
            onChange={this.onChange}
          />
        </FormField>
        <FormField>
          <Input
            label='Password'
            placeholder='Password...'
            labelPosition='left'
            type='password'
            value={this.state.password}
            name='password'
            onChange={this.onChange}
          />
        </FormField>
        <FormField>
          <Input
            label='Password Confirmation'
            placeholder='Password Confirmation'
            labelPosition='left'
            type='password'
            value={this.state.password2}
            name='password2'
            onChange={this.onChange}
          />
        </FormField>
        <Input
          color='red'
          type='submit'
          // as='button'
          // action={<Button  />}
          value='Submit'
        />
      </Form>
    );
  }
}
