import React from 'react';
import {
  Form,
  Header,
  Input,
  Image,
  Button,
  Grid,
  Segment,
} from 'semantic-ui-react';

class Search extends React.Component {
  state = {
    address: '',
    query: '',
    distance: '',
  };

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  onSubmit = (e) => {
    const formData = this.state;
    e.preventDefault();
    this.props.googleSearch(formData);
    this.props.loadingHandler();
  };

  render() {

    return (
      <div>
        <Grid
          textAlign='center'
          style={{ height: '100vh' }}
          verticalAlign='middle'
        >
          <Grid.Column style={{ maxWidth: 900 }}>
            <Header as='h2' color='red' textAlign='center'>
              <Image src='../favicon.ico' />
              Telemed
            </Header>
            {this.props.error && (
              <Segment>No search results found / API is currently down</Segment>
            )}
            <Form onSubmit={this.onSubmit}>
              <Input
                required
                name='address'
                onChange={(e) => this.onChange(e)}
                type='text'
                placeholder='Address'
                value={this.state.address}
              ></Input>
              <Input
                required
                name='query'
                onChange={(e) => this.onChange(e)}
                type='text'
                placeholder='What hurts?'
                value={this.state.query}
              ></Input>
              <Input
                required
                name='distance'
                onChange={(e) => this.onChange(e)}
                type='number'
                placeholder='...in miles'
                value={this.state.distance}
              >Distance </Input>

              {!this.props.isLoading && (
                <Button color='red' type='submit' value='Get Doctors'>
                  Get Doctors
                </Button>
              )}
              {this.props.isLoading && (
                <Button loading color='red' type='submit' value='Get Doctors'>
                  Get Doctors
                </Button>
              )}
            </Form>
          </Grid.Column>
        </Grid>
      </div>
    );
  }
}

export default Search;
