import React from 'react';
import { Card, Grid } from 'semantic-ui-react';
import DoctorCard from '../components/DoctorCard';
import { withRouter } from 'react-router-dom';

class Doctors extends React.Component {
  state = {
    address: '',
    query: '',
    distance: '',
  };

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  onSubmit = (e) => {
    const formData = this.state;
    e.preventDefault();
    this.props.toGeoCode(formData);
  };

  render() {
    const searchedDoctors = this.props.searchedDoctors;
    let filteredDocs = this.props.doctors.map((doctor) => {
      if (searchedDoctors && searchedDoctors.includes(doctor.place_id)) {
        return doctor
      }
    });
    let displayDoctors = this.props.doctors.map((doctor) => (

      <DoctorCard
        key={doctor.id}
        getPlaceDetails={this.props.getPlaceDetails}
        doctor={doctor}
      />
    ));
    return (

      <div>
        <Grid
          textAlign='center'
          style={{ height: '100vh' }}
          verticalAlign='middle'
        >
          <br></br>
          <Card.Group
            style={{ overflow: 'auto', maxHeight: '65em' }}
            display='flex'
            justify-content='center'
            itemsPerRow={6}
          >
            {displayDoctors}
          </Card.Group>
        </Grid>
      </div>
    );
  }
}

export default withRouter(Doctors);
