import React from 'react';
import {
  Grid,
  Rating,
  Header,
  Message,
  Divider,
  Segment,
  Image,
  GridColumn,
} from 'semantic-ui-react';
import axios from 'axios';

class DoctorShow extends React.Component {
  state = {
    favorite: 0,
    currentUser: null,
    doc: {},
    id: null,
  };

  isFavorite = () => {
    const favorite = this.props.currentUser.doctors.find(
      (doc) => doc.id === parseInt(this.props.match.params.id)
    );
    if (favorite) this.setState({ favorite: 1 });
  };

  async componentDidMount() {
    await this.props.getDoctorById(parseInt(this.props.match.params.id));
    await this.props.loadUser();
    this.isFavorite();

    // if(this.props.currentUser.doctors.includes(this.props.doctorShow)) this.setState({favorite: 1})
  }

  postFavorite = async () => {
    const favoriteObject = {
      doctor_id: this.props.match.params.id,
      user_id: this.props.currentUser.id,
    };
    try {
      let res = await axios.post(
        `http://localhost:3000/favorites`,
        favoriteObject
      );
      this.setState({ favorite: 1, id: res.data.id }, () => {
        this.props.loadUser();
      });
      console.log(res.data);
    } catch (error) {}
  };

  deleteFavorite = async () => {
    let favorite = this.props.currentUser.favorites.find(
      (favorite) => favorite.doctor_id === parseInt(this.props.match.params.id)
    );
    if (favorite === -1) {
      this.setState({ favorite: 0 });
    }
    try {
      let res = await axios.delete(
        `http://localhost:3000/favorites/${favorite.id}`
      );
      console.log('res.data from deletefavorite', res.data);
      this.setState({ favorite: 0, id: null }, () => {
        this.props.loadUser();
      });
    } catch (err) {
      console.log('error', err);
    }

    console.log('got here in delete');
  };

  rate = (e, { rating }) => {
    if (rating === 0) {
      this.deleteFavorite();
    } else {
      this.postFavorite();
    }
  };

  render() {
    console.log('DOCTOR SHOW THIS.STATE: ', this.state);
    // debugger
    const {
      name,
      formatted_phone_number,
      formatted_address,
      rating,
      url,
      website,
      reviews,
      photos,
      photo
    } = this.props.doctorShow;

    return (
      <div>
        <Segment textAlign='center'>
          <div >
            <Header>
              <a
                href={website}
                rel='noopener noreferrer'
                target='_blank'
                to={website}
              >
                {name}
              </a>
            </Header>
            <Image src={photos ? photos[0].slug : ''}></Image>
          </div>

          <Rating
            onRate={(e, data) => this.rate(e, data)}
            icon='heart'
            rating={this.state.favorite}
            maxRating={1}
            size='huge'
          />
          <Divider />
          <Message>
            <a rel='noopener noreferrer' href={`tel:${formatted_phone_number}`}>
              {formatted_phone_number}
            </a>
          </Message>
          <Message>
            <a href={url} rel='noopener no referrer' target='_blank'>
              {formatted_address}
            </a>
          </Message>

          {rating && <Message>Average Rating: {rating}</Message>}
        </Segment>

        <Grid
          columns={2}
          textAlign='center'
          // style={{ height: '100vh' }}
          verticalAlign='top'
        >
          <Grid.Row>
            {reviews &&
              reviews.map((review) => (
                <Message key={review.author_name}>
                  <span>
                    <strong>{review.author_name}</strong>
                  </span>
                  <p>{review.text}</p>
                </Message>
              ))}
          </Grid.Row>

          {photos &&
            photos.map((photo) => (
              <Image
                size='medium'
                key={photo.id}
                alt=''
                src={photo.slug}
              ></Image>
            ))}
        </Grid>
      </div>
    );
  }
}

export default DoctorShow;
