                <Divider />
                <Message>
                  Phone Number: {formatted_phone_number}
                  Address: {formatted_address}
                  Website: {website}
                  <Link as='a' to={url}></Link>
                  {rating}
                  {user_ratings_total}
                  {formatted_address}
                </Message>
                <Rating
                  onRate={(e, data) => this.rate(e, data)}
                  icon='heart'
                  rating={this.state.favorite}
                  maxRating={1}
                  size='huge'
                />
              </Segment>
            </Grid.Column>
            <Grid.Column>
              <Message>{business_status}</Message>
