import React from 'react';

import {
  Header,
  Divider,
  Button,
  Form,
  Input,
  Grid,
  Card,
  Icon,
  Container,
} from 'semantic-ui-react';

import DoctorCard from '../DoctorCard';
import PropTypes from 'prop-types';
import Edit from './Edit';

const Profile = ({ currentUser, loadUser, history, loading }) => {
  const profileDoctors = () =>
    currentUser.doctors &&
    currentUser.doctors.map((doc) => <DoctorCard key={doc.id} doctor={doc} />);

  return (
    <>

      {currentUser && (

        <Container>
          {currentUser && (
            <>
              <Header as='h4' display='flex' textAlign='center' color='red'>
                <Icon loading={loading} size='tiny' name='edit'></Icon>
                {currentUser.email}
              </Header>
            </>
          )}
          <Divider />{' '}
          <Header as='h2' display='flex' textAlign='center' color='red'>
            Favorite Doctors
          </Header>
          <Divider />
          <Card.Group
            style={{ overflow: 'auto', maxHeight: '52em' }}
            display='flex'
            justify-content='center'
            itemsPerRow={3}
          >
            {profileDoctors()}
          </Card.Group>
        </Container>
        // </Grid.Row>
      )}
      {/* </Grid> */}
    </>
  );
};

Profile.propTypes = {
  loadUser: PropTypes.func.isRequired,
  currentUser: PropTypes.object.isRequired,
};

export default Profile;
