import React from 'react';
import PropTypes from 'prop-types';
import { Form, FormField, Label, Input } from 'semantic-ui-react';
import { useEffect } from 'react';
import { useState } from 'react';

const Edit = (props) => {
  const { currentUser, loadUser, patchUser } = props;
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    password2: '',
  });
  useEffect(() => {
    loadUser();
  }, [loadUser]);

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });

    console.log(formData);
  };

  const onSubmit = async () => {};

  const initialState = {};
  console.log(formData);
  return (
    <Form display='flex' onSubmit={onSubmit}>
      <FormField>
        <Input
          disabled={currentUser}
          label='First Name'
          placeholder={
            currentUser
              ? currentUser.firstName
                ? currentUser.firstName
                : 'John'
              : null
          }
          labelPosition='left'
          type='text'
          value={formData.firstName}
          name='firstName'
          onChange={onChange}
        ></Input>
      </FormField>
      <FormField>
        <Input
          label='Last Name'
          placeholder={
            currentUser
              ? currentUser.lastName
                ? currentUser.lastName
                : 'Doe'
              : null
          }
          labelPosition='left'
          type='text'
          value={formData.lastName}
          name='lastName'
          onChange={onChange}
        ></Input>
      </FormField>
      <FormField>
        <Input
          label='Email'
          placeholder={
            currentUser
              ? currentUser.email
                ? currentUser.email
                : 'Email...'
              : null
          }
          labelPosition='left'
          type='email'
          value={formData.lastName}
          name='email'
          onChange={onChange}
        ></Input>
      </FormField>
      <FormField>
        <Input
          error
          label='Address'
          placeholder={
            currentUser
              ? currentUser.address
                ? currentUser.address
                : '123 Main Street'
              : null
          }
          labelPosition='left'
          type='text'
          value={formData.address}
          name='address'
          onChange={onChange}
        ></Input>
      </FormField>
      <FormField>
        <Input
          label='Password'
          placeholder='Password...'
          labelPosition='left'
          type='password'
          value={formData.password}
          name='password'
          onChange={onChange}
        ></Input>
      </FormField>
      <FormField>
        <Input
          label='Password Confirmation'
          placeholder='Password Confirmation'
          labelPosition='left'
          type='password'
          value={formData.password2}
          name='password2'
          onChange={onChange}
        ></Input>
      </FormField>
    </Form>
  );
};

Edit.propTypes = {
  patchUser: PropTypes.func.isRequired,
  loadUser: PropTypes.func.isRequired,
  currentUser: PropTypes.object.isRequired,
};

export default Edit;
