import React from 'react';
import PropTypes from 'prop-types';
import {  Grid,
  Rating,
  Header,
  Message,
  Divider,
  Segment,
  Image,
  } from 'semantic-ui-react'

const About = ({ doctor }) => {
  return (
    <Segment textAlign='center'>
      <Header
        as='a'
        href={website}
        rel='noopener noreferrer'
        target='_blank'
        content={name}
      />

      <Rating
        onRate={(e, data) => this.rate(e, data)}
        icon='heart'
        rating={this.state.favorite}
        maxRating={1}
        size='huge'
      />
      <Divider hidden />
      <Message>
        <a rel='noopener noreferrer' href={`tel:${formatted_phone_number}`}>
          {formatted_phone_number}
        </a>
      </Message>
      <Message>
        <a href={url} rel='noopener no referrer' target='_blank'>
          {formatted_address}
        </a>
      </Message>

      {rating && (
        <Message>
          <strong>
            Average Rating:{' '}
            {<Rating disabled icon='star' maxRating={5} rating={rating} />}
          </strong>
        </Message>
      )}
    </Segment>
  );
};

About.propTypes = {
  doctor: PropTypes.object.isRequired,
};

export default About;
