import React from 'react';
import {
  Grid,
  Rating,
  Header,
  Message,
  Divider,
  Segment,
  Image,
  GridColumn,
  ItemImage,
} from 'semantic-ui-react';
import axios from 'axios';

class DoctorShow extends React.Component {
  state = {
    favorite: 0,
  };

  isFavorite = () => {
    const favorite = this.props.currentUser.doctors.find(
      (doc) => doc.id === parseInt(this.props.match.params.id)
    );
    if (favorite) this.setState({ favorite: 1 });
  };

  async componentDidMount() {
    await this.props.getDoctorById(parseInt(this.props.match.params.id));
    await this.props.loadUser();
    this.isFavorite();

    // if(this.props.currentUser.doctors.includes(this.props.doctorShow)) this.setState({favorite: 1})
  }

  postFavorite = async () => {
    const favoriteObject = {
      doctor_id: this.props.match.params.id,
      user_id: this.props.currentUser.id,
    };
    try {
      let res = await axios.post(
        `http://localhost:3000/favorites`,
        favoriteObject
      );
      this.setState({ favorite: 1, id: res.data.id }, () => {
        this.props.loadUser();
      });
      console.log(res.data);
    } catch (error) {}
  };

  deleteFavorite = async () => {
    let favorite = this.props.currentUser.favorites.find(
      (favorite) => favorite.doctor_id === parseInt(this.props.match.params.id)
    );
    if (favorite === -1) {
      this.setState({ favorite: 0 });
    }
    try {
      let res = await axios.delete(
        `http://localhost:3000/favorites/${favorite.id}`
      );
      console.log('res.data from deletefavorite', res.data);
      this.setState({ favorite: 0, id: null }, () => {
        this.props.loadUser();
      });
    } catch (err) {
      console.log('error', err);
    }

    console.log('got here in delete');
  };

  rate = (e, { rating }) => {
    if (rating === 0) {
      this.deleteFavorite();
    } else {
      this.postFavorite();
    }
  };

  render() {
    console.log('DOCTOR SHOW THIS.STATE: ', this.state);
    // debugger
    const {
      name,
      formatted_phone_number,
      formatted_address,
      rating,
      url,
      website,
      reviews,
      photos,
      photo,
    } = this.props.doctorShow;

    return (
      <div>
        <Image centered size='large' src={photo && photo}></Image>
        <Segment textAlign='center'>
          <Header
            as='a'
            href={website}
            rel='noopener noreferrer'
            target='_blank'
            content={name}
          ></Header>

          <Rating
            onRate={(e, data) => this.rate(e, data)}
            icon='heart'
            rating={this.state.favorite}
            maxRating={1}
            size='huge'
          />
          <Divider hidden />
          <Message>
            <a rel='noopener noreferrer' href={`tel:${formatted_phone_number}`}>
              {formatted_phone_number}
            </a>
          </Message>
          <Message>
            <a href={url} rel='noopener no referrer' target='_blank'>
              {formatted_address}
            </a>
          </Message>
          <Message>
            <strong>
              Average Rating:{' '}
              {
                <Rating disabled icon='star' maxRating={5} rating={rating}>
                  {/* {review.rating} */}
                </Rating>
              }
            </strong>
          </Message>
        </Segment>

        <Grid
          columns={2}
          centered
          textAlign='center'
          style={{ height: '100vh' }}
          verticalAlign='top'
        >
          <Segment></Segment>
          <Grid.Row>
            {reviews &&
              reviews.map((review) => (
                <Message>
                  <div key={review.author_name}>
                    <div>
                      <Image
                        floated='left'
                        spaced='left'
                        as='icon'
                        size='mini'
                        src={review.profile_photo_url}
                      ></Image>
                      <Rating
                        disabled
                        icon='star'
                        maxRating={5}
                        rating={review.rating}
                      ></Rating>
                    </div>

                    <p>{review.text}</p>
                  </div>
                  <div>
                    <a href={review.author_url} rel='no referrer'>
                      <>{review.author_name}</>
                    </a>
                  </div>
                </Message>
              ))}
          </Grid.Row>
          <Grid.Row>
            <Image.Group size='medium'>
              {photos &&
                photos.map((photo) => (
                  <Image key={photo.id} alt='' src={photo.slug} />
                ))}
            </Image.Group>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default DoctorShow;
