import React, { Component } from 'react';

import { Form, FormField, Label, Input, Button } from 'semantic-ui-react';

export default class Edit extends Component {
  state = {
    first_name: "",
    last_name: "",
    email: "",
    address: "",
  };

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onSubmit = async (e) => {
    const formData = this.state;
    e.preventDefault();
    await this.props.patchUser(formData);
  };
  componentDidMount(){
    const {email, address, first_name, last_name} = this.props.currentUser
    this.props.loadUser()
    this.setState({email, address, first_name, last_name })
  }

  render() {
    console.log('PROPS FROM EDITJS', this.props);
    console.log('state FROM EDITJS', this.state);

    return (
      <Form style={{ padding: '3em' }} onSubmit={this.onSubmit} display='flex'>
        <FormField>
          <Input
            label='First Name'
            placeholder={
              this.props.currentUser && this.props.currentUser.first_name
                ? this.props.currentUser.first_name
                : 'John'
            }
            labelPosition='left'
            type='text'
            value={this.state.first_name}
            name='first_name'
            onChange={this.onChange}
          />
        </FormField>
        <FormField>
          <Input
            label='Last Name'
            placeholder={
              this.props.currentUser && this.props.currentUser.last_name
                ? this.props.currentUser.last_name
                : 'Doe  '
            }
            labelPosition='left'
            type='text'
            value={this.state.last_name}
            name='last_name'
            onChange={this.onChange}
          />
        </FormField>
        <FormField>
          <Input
            label='Email'
            placeholder={
              this.props.currentUser && this.props.currentUser.email
                ? this.props.currentUser.email
                : 'Email...'
            }
            // labelPosition='left'
            type='email'
            value={this.state.email}
            name='email'
            onChange={this.onChange}
          />
        </FormField>
        <FormField>
          <Input
            label='Address'
            placeholder={
              this.props.currentUser
                ? this.props.currentUser.address
                  ? this.props.currentUser.address
                  : '123 Main Street'
                : null
            }
            labelPosition='left'
            type='text'
            value={this.state.address}
            name='address'
            onChange={this.onChange}
          />
        </FormField>
        {/*<FormField>
          <Input
            label='Password'
            placeholder='Password...'
            labelPosition='left'
            type='password'
            value={this.state.password}
            name='password'
            onChange={this.onChange}
          />
        </FormField>
        <FormField>
          <Input
            label='Password Confirmation'
            placeholder='Password Confirmation'
            labelPosition='left'
            type='password'
            value={this.state.password2}
            name='password2'
            onChange={this.onChange}
          />
          </FormField> */}
        <Input
          color='red'
          type='submit'
          // as='button'
          // action={<Button  />}
          value='Submit'
        />
      </Form>
    );
  }
}
