import React from 'react';

export const createDoctors = async (incomingDoctors) => {
  await incomingDoctors.map(async (doctor) => {
    let detailedDoctor;

    try {
      let res = await axios.get(
        `http://localhost:3000/geocodes/details/${doctor.place_id}`
      );
      detailedDoctor = res.data.result;
    } catch (err) {
      console.log(err, 'FROM CREATEDOCTORS HELPER');
      throw err;
    }

    // destructuring for values in schema
    const {
      formatted_address,
      formatted_phone_number,
      name,
      place_id,
      rating,
      url,
      website,
      user_ratings_total,
      reviews,
      photos,
      geometry: {
        location: { lat, lng },
      },
    } = detailedDoctor;

    // ES6 key value assignment operator

    const doctorHash = {
      place_id,
      name,
      formatted_address,
      rating,
      user_ratings_total,
      formatted_phone_number,
      lat,
      lng,
      url,
      website,
      photos,
      reviews,
    };

    // create post headers
    const myHeaders = new Headers();

    myHeaders.append('Content-Type', 'Application/json');
    myHeaders.append('accepts', 'application/json');

    // TODO check into stringify
    // TODO check into redirect: follow
    // options hash for post request to api

    let data;
    let photoData;

    // CREATE DOCTOR

    try {
      let res = await axios.post('http://localhost:3000/doctors', doctorHash);
      data = res.data;

      console.log(data, 'DATA IN CREATE DOCTOR');
      let reviewData;



      const photoPayload = {
        incomingPhotos: photos,
        doctorId: data.id,
        placeId: place_id,
      };

      try {
        photoData = await this.createPhotos(photoPayload);
        console.log(photoData);
      } catch (err) {
        console.log(err);
      }
    } catch (err) {
      console.log(err);
    }
  });
};

export const createReviews = () => {
  reviews.forEach(async (singleReview) => {
    const {
      author_name,
      author_url,
      profile_photo_url,
      rating,
      relative_time_description,
      text,
      time,
    } = singleReview;
    const reviewHash = {
      author_name,
      author_url,
      profile_photo_url,
      rating,
      relative_time_description,
      text,
      time,
      place_id,
      doctor_id: data.id,
    };

    let res = await axios.post('http://localhost:3000/reviews', reviewHash);

    reviewData = res.data;
    console.log('reviewData from reviewPost', reviewData);

    //
  });
};

export const createPhotos = async ({ doctorId, placeId }) => {
  let data = [];
  const myHeaders = new Headers();

  const photoHash = {
    doctor_id: doctorId,
    doctor_reference: placeId,
  };

  try {
    let res = await axios.post(`http://localhost:3000/photos`, photoHash);

    data = res.data;
  } catch (err) {
    console.log(err);
    throw err;
  }
  return data;
};
