  match,
  loadUser,
  currentUser,
  loadingHandler,
  loading,
  setSearchIndex,
  searchIndex,
}) => {
  const index = [];
  const loadSearchIndex = async () => {
    let csv = '';
    try {
      let res = await Axios.get(`http://localhost:3000/searches/${searchId}`);
      csv = res.data.csv;
    } catch (err) {}
