    csv.pop();
    console.log(csv, 'CSV FROM LOADSEARCHINDEX');
    await csv.forEach(async (id) => {
      console.log(id);
      let doctorId = parseInt(id);
      let res = await Axios.get(`http://localhost:3000/doctors/${doctorId}`);
      let doc = res.data;
      console.log(doc);
      index.push(doc);
    });
    console.log(index);
    await setSearchIndex(index);
  };
  const displayDoctors = () => {
    console.log(searchIndex);
    return searchIndex.map((doc) => <div key={doc.id}>{doc.name}</div>);
  };

  const {
    params: { userId, searchId },
  } = match;

  console.log(userId, searchId);

