import React from 'react';
import {
  Grid,
  Rating,
  Header,
  Message,
  Divider,
  Segment,
} from 'semantic-ui-react';

class DoctorShow extends React.Component {
  isFavorite = () => {
    // eslint-disable-next-line
    let favoriteArray = this.props.currentUser.doctors.filter(
      // eslint-disable-next-line
      (doctor) => doctor.id == this.props.match.params.id
    );
    if (favoriteArray.length > 0) {
      this.setState({ favorite: 1 });
    }
  };

  state = {
    favorite: 0,
  };

  favoriteHandler = () => {
    this.setState({
      favorite: 0,
    });
  };

  docCheck = () => {
    let doc;
    if (this.props.doctors.length > 0) {
      // eslint-disable-next-line
      doc = this.props.doctors.find(
        // eslint-disable-next-line
        (doctor) => doctor.id == this.props.match.params.id
      );
      if (!!doc) {
        this.renderDocJsx(doc);
      }
    } else {
      doc = this.props.currentUser.doctors.find(
        // eslint-disable-next-line
        (doctor) => doctor.id == this.props.match.params.id
      );
      if (!!doc) {
        this.renderDocJsx(doc);
      }
    }
  };

  componentDidMount() {
    if (Array.isArray(this.props.currentUser.doctors)) {
      this.isFavorite();
    }
  }

  rate = (e, data, doc) => {
    this.setState(
      {
        favorite: data.rating,
      },
      () => this.props.heart(doc)
    );
  };
  displayReviews = ({ reviews }) => {
    reviews.map((review) => console.log(review));
  };

  render() {
    const {
      business_status,
      formatted_address,
      formatted_phone_number,
      name,
      rating,
      reviews,
      website,
      user_ratings_total,
      url,
    } = this.props.details.data.result;

    //     address_components: (9) [{…}, {…}, {…}, {…}, {…}, {…}, {…}, {…}, {…}]
    // adr_address: "<span class="street-address">2097 Henry Tecklenburg Dr #210</span>, <span class="locality">Charleston</span>, <span class="region">SC</span> <span class="postal-code">29414</span>, <span class="country-name">USA</span>"
    // business_status: "OPERATIONAL"
    // formatted_address: "2097 Henry Tecklenburg Dr #210, Charleston, SC 29414, USA"
    // formatted_phone_number: "(843) 226-7996"
    // geometry: {location: {…}, viewport: {…}}
    // icon: "https://maps.gstatic.com/mapfiles/place_api/icons/generic_business-71.png"
    // id: "0992401808a37c51299815a972016597c59169ad"
    // international_phone_number: "+1 843-226-7996"
    // name: "Associated Foot Specialists: Charles J. Gudas, DPM"
    // opening_hours: {open_now: false, periods: Array(5), weekday_text: Array(7)}
    // place_id: "ChIJzbmYNVd8_ogRNmSIH9nDYPE"
    // plus_code: {compound_code: "RX55+94 Charleston, SC, United States", global_code: "864XRX55+94"}
    // rating: 4.6
    // reference: "ChIJzbmYNVd8_ogRNmSIH9nDYPE"
    // reviews: (5) [{…}, {…}, {…}, {…}, {…}]
    // scope: "GOOGLE"
    // types: (4) ["doctor", "health", "point_of_interest", "establishment"]
    // url: "https://maps.google.com/?cid=17393117098209207350"
    // user_ratings_total: 11
    // utc_offset: -240
    // vicinity: "2097 Henry Tecklenburg Drive #210, Charleston"
    // website: "https://www.associatedfootspecialistspa.com/?utm_sou

    let doc;
    doc = this.props.currentUser.doctors.find(
      // eslint-disable-next-line
      (doctor) => doctor.id == this.props.match.params.id
    );
    // eslint-disable-next-line
    if (doc == undefined) {
      // eslint-disable-next-line
      doc = this.props.doctors.find(
        // eslint-disable-next-line
        (doctor) => doctor.id == this.props.match.params.id
      );
    }

    return (
      <div>
        <Grid
          columns={2}
          textAlign='center'
          style={{ height: '100vh' }}
          verticalAlign='middle'
        >
          <Grid.Row style={{ maxWidth: 1000 }}>
            <Grid.Column>
              <Segment>
                <Header>{name}</Header>
                <Rating
                  onRate={(e, data) => this.rate(e, data)}
                  icon='heart'
                  rating={this.state.favorite}
                  maxRating={1}
                  size='huge'
                />
                <Divider />
                <Message>Phone Number: {formatted_phone_number}</Message>
                <Message>Address: {formatted_address}</Message>
                <Message>
                  <a
                    href={website}
                    rel='noopener noreferrer'
                    target='_blank'
                    to={website}
                  >
                    Website
                  </a>
                </Message>
                <Message>
                  <a href={url} rel=''>
                    Google Maps
                  </a>
                </Message>
                <Message>{rating}</Message>
                <Message>{rating}</Message>
                {user_ratings_total}
              </Segment>
            </Grid.Column>
            <Grid.Column>{/* {this.displayReviews} */}</Grid.Column>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default DoctorShow;
