import React from 'react';

import {
  Header,
  Divider,
  Button,
  Form,
  Input,
  Grid,
  Card,
  Icon,
} from 'semantic-ui-react';

import DoctorCard from '../DoctorCard';
import { Link } from 'react-router-dom';
import Edit from './Edit';

class Profile extends React.Component {
  state = {};

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  profileDoctors = () => {
    if (this.props.currentUser.doctors) {
      return this.props.currentUser.doctors.map((doc) => (
        <DoctorCard key={doc.id} doctor={doc} />
      ));
    } else {
      this.props.history.push('/login');
    }
  };

  async componentDidMount() {
    await this.props.loadUser();
    // this.setForm();
  }

  render() {
    console.log('THIS.STATE PROFILE: ', this.state);
    const {currentUser } = this.props
    return (
      <>
        <Grid style={{ padding: '2em' }} columns={2}>
          <Grid.Row>
            {this.props.currentUser && (
              <>
                <Header display='flex' textAlign='center' color='red'>
                  <Icon size='tiny' name='edit'></Icon>
                  {this.props.currentUser.email}
                </Header>
              </>
            )}
            <Divider />
          </Grid.Row>
          {this.props.currentUser && (
            <Grid.Row>
              <Header as='h2' display='flex' textAlign='center' color='red'>
                Favorite Doctors
              </Header>
              <Divider />
              <Card.Group
                style={{ overflow: 'auto', maxHeight: '52em' }}
                display='flex'
                justify-content='center'
                itemsPerRow={1}
              >
                {this.profileDoctors()}
              </Card.Group>
            </Grid.Row>
          )}
        </Grid>
      </>
    );
  }
}
export default Profile;
