import React from 'react';
import {
  Grid,
  Rating,
  Header,
  Message,
  Divider,
  Segment,
  Image,
} from 'semantic-ui-react';

class DoctorShow extends React.Component {
  isFavorite = () => {
    // eslint-disable-next-line
    let favoriteArray = this.props.currentUser.doctors.filter(
      // eslint-disable-next-line
      (doctor) => doctor.id == this.props.match.params.id
    );
    if (favoriteArray.length > 0) {
      this.setState({ favorite: 1 });
    }
  };

  state = {
    favorite: 0,
  };

  favoriteHandler = () => {
    this.setState({
      favorite: 0,
    });
  };

  docCheck = () => {
    let doc;
    if (this.props.doctors.length > 0) {
      // eslint-disable-next-line
      doc = this.props.doctors.find(
        // eslint-disable-next-line
        (doctor) => doctor.id == this.props.match.params.id
      );
      if (!!doc) {
        this.renderDocJsx(doc);
      }
    } else {
      doc = this.props.currentUser.doctors.find(
        // eslint-disable-next-line
        (doctor) => doctor.id == this.props.match.params.id
      );
      if (!!doc) {
        this.renderDocJsx(doc);
      }
    }
  };

  componentDidMount() {
    if (Array.isArray(this.props.currentUser.doctors)) {
      this.isFavorite();
    }
  }

  rate = (e, data, doc) => {
    this.setState(
      {
        favorite: data.rating,
      },
      () => this.props.heart(doc)
    );
  };

  render() {
    console.log(this.props)
    debugger;
    // const {
    //   vicinity,
    //   phone,
    //   name,
    //   rating,
    //   reviews,
    //   website,
    //   user_ratings_total,
    //   url,
    //   photos
    // } = this.props.details;
    //     api_id: "ChIJE7L9Gj96_ogRZPALM1rd7sI"
    // favorites: []
    // id: null
    // name: "Dr. Terrence A. Day, MD"
    // opening_hours: null
    // phone: "(843) 792-8363"
    // photos: (6) ["https://maps.googleapis.com/maps/api/place/photo?m…Nawow&key=AIzaSyCuEOeSzUNgNIoiSLtWMZdIUPkl0S3DTDI", "https://maps.googleapis.com/maps/api/place/photo?m…eA4pA&key=AIzaSyCuEOeSzUNgNIoiSLtWMZdIUPkl0S3DTDI", "https://maps.googleapis.com/maps/api/place/photo?m…zBnoA&key=AIzaSyCuEOeSzUNgNIoiSLtWMZdIUPkl0S3DTDI", "https://maps.googleapis.com/maps/api/place/photo?m…_nMSw&key=AIzaSyCuEOeSzUNgNIoiSLtWMZdIUPkl0S3DTDI", "https://maps.googleapis.com/maps/api/place/photo?m…RPS1Q&key=AIzaSyCuEOeSzUNgNIoiSLtWMZdIUPkl0S3DTDI", "https://maps.googleapis.com/maps/api/place/photo?m…aHwDg&key=AIzaSyCuEOeSzUNgNIoiSLtWMZdIUPkl0S3DTDI"]
    // rating: 2.7
    // user_ratings_total: 3
    // users: []
    // vicinity: "135 Rutledge Ave, Charleston, SC 29403, USA"

    let doc;
    doc = this.props.currentUser.doctors.find(
      // eslint-disable-next-line
      (doctor) => doctor.id == this.props.match.params.id
    );
    // eslint-disable-next-line
    if (doc == undefined) {
      // eslint-disable-next-line
      doc = this.props.doctors.find(
        // eslint-disable-next-line
        (doctor) => doctor.id == this.props.match.params.id
      );
    }

    return (
      <div>
        <Grid
          columns={2}
          textAlign='center'
          style={{ height: '100vh' }}
          verticalAlign='middle'
        >
          <Grid.Row style={{ maxWidth: 1000 }}>
            <Grid.Column>
              <Segment>
                <Header>{name}</Header>
                <Rating
                  onRate={(e, data) => this.rate(e, data)}
                  icon='heart'
                  rating={this.state.favorite}
                  maxRating={1}
                  size='huge'
                />
                <Divider />
                <Message>Phone Number: {phone}</Message>
                <Message>Address: {vicinity}</Message>
                <Message>
                  <a
                    href={website}
                    rel='noopener noreferrer'
                    target='_blank'
                    to={website}
                  >
                    Website
                  </a>
                </Message>
                <Message>
                  <a href={url} rel=''>
                    Google Maps
                  </a>
                </Message>
                {rating && <Message>Average Rating: {rating}</Message>}
                {user_ratings_total && (
                  <Message>Total Ratings{user_ratings_total}</Message>
                )}
                {reviews &&
                  reviews.map((review) => (
                    <div key={review.author_name}>
                      <Message>
                        <span>
                          <strong>{review.author_name}</strong>
                        </span>
                        <p>{review.text}</p>
                      </Message>
                    </div>
                  ))}
              </Segment>
            </Grid.Column>
            <Grid.Column><Image alt='' src={photos[0]}></Image></Grid.Column>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default DoctorShow;
