import React, { Component } from 'react';

import { Form, FormField, Label, Input, Button } from 'semantic-ui-react';

export default class Edit extends Component {
  state = {
    first_name: this.props.currentUser.first_name || "",
    last_name: "",
    address: "",
    email: ""

  };

  onChange = (e )=> {
    this.setState({

      [e.target.name]: [e.target.value]
    })
  }


  render() {
    console.log('PROPS FROM EDITJS', this.props);
    console.log('state FROM EDITJS', this.state);

    return (
      <Form style={{ padding: '3em' }} onSubmit={(e)=>this.props.editProfileOnSubmit(e,this.state)} display='flex'>
        <FormField>
          <Input
            label='First Name'
            placeholder={
              this.props.currentUser && this.props.currentUser.first_name
                ? this.props.currentUser.first_name
                : 'John'
            }
            labelPosition='left'
            type='text'
            value={this.props.currentUser.first_name}
            name='first_name'
            onChange={this.props.editProfileOnChange}
          />
        </FormField>
        <FormField>
          <Input
            label='Last Name'
            placeholder={
              this.props.currentUser && this.props.currentUser.last_name
                ? this.props.currentUser.last_name
                : 'Doe  '
            }
            labelPosition='left'
            type='text'
            value={this.props.currentUser.last_name}
            name='last_name'
            onChange={this.props.editProfileOnChange}
          />
        </FormField>
        <FormField>
          <Input
            label='Email'
            placeholder={
              this.props.currentUser && this.props.currentUser.email
                ? this.props.currentUser.email
                : 'Email...'
            }
            labelPosition='left'
            type='email'
            value={this.props.currentUser.email}
            name='email'
            onChange={this.props.editProfileOnChange}
          />
        </FormField>
        <FormField>
          <Input
            label='Address'
            placeholder={
              this.props.currentUser
                ? this.props.currentUser.address
                  ? this.props.currentUser.address
                  : '123 Main Street'
                : null
            }
            labelPosition='left'
            type='text'
            value={this.props.currentUser.address}
            name='address'
            onChange={this.props.editProfileOnChange}
          />
        </FormField>
        {/*<FormField>
          <Input
            label='Password'
            placeholder='Password...'
            labelPosition='left'
            type='password'
            value={this.props.currentUser.password}
            name='password'
            onChange={this.onChange}
          />
        </FormField>
        <FormField>
          <Input
            label='Password Confirmation'
            placeholder='Password Confirmation'
            labelPosition='left'
            type='password'
            value={this.props.currentUser.password2}
            name='password2'
            onChange={this.onChange}
          />
          </FormField> */}
        <Input
          color='red'
          type='submit'
          // as='button'
          // action={<Button  />}
          value='Submit'
        />
      </Form>
    );
  }
}
